    /* Navbar CSS */

    .flatworm-nav {
        --text-shadow: rgba(var(--black-monochrome), 0.35);
        --box-shadow: rgb(var(--gray-monochrome));
        --border-color: rgb(var(--newpage-color));
        color: rgb(var(--white-monochrome));
        text-shadow: 0.0625rem 0.0625rem 0.125rem var(--text-shadow);
        border-radius: 0.0625rem;
        border: double 0.188rem var(--border-color);
        background-color: rgb(var(--bright-accent));
        box-shadow: 0.09375rem 0.09375rem 0.188rem var(--box-shadow);
        padding: 0.3rem 0.125rem;
        margin: 0.625rem 0 0.938rem;
        margin-bottom: 1.250rem;
    }

    .flatworm-nav a {
        color: rgb(255, 237, 6);
        display: inline-block;
        position: relative;
        width: 100%;
        height: 100%;
        padding-top: 0.45em;
        padding-bottom: 0.35em;
    }

    .flatworm-nav a::before {
        display: flex;
        position: absolute;
        align-items: center;
        height: 100%;
        font-size: calc(var(--base-font-size) * 1.1);
        line-height: calc(var(--base-font-size) * 0.7);
        font-weight: 800;
        top: 0;
    }

    .flatworm-nav .footer-wikiwalk-nav {
        display: flex;
        align-items: center;
        justify-content: center;
        font-weight: 400;
        letter-spacing: 0.025em;
        font-size: 75%;
    }

    .flatworm-nav .footer-wikiwalk-nav * {
        line-height: 1em;
    }

    .flatworm-nav .footer-wikiwalk-nav .center {
        flex-basis: 8em;
        text-align: center; 
        font-size: 120%;
    }

    .flatworm-nav .footer-wikiwalk-nav .left,
    .flatworm-nav .footer-wikiwalk-nav .right {
        flex-basis: calc(50% - 5em);
        display: inline-flex;
        align-items: center;
        font-size: calc(var(--base-font-size) * 0.8);
        position: relative;
    }

    .flatworm-nav .footer-wikiwalk-nav .left {
        text-align: right;
        justify-content: flex-end;
        border-right: 0.0625rem solid rgb(var(--white-monochrome));
        padding-left: 1em;
        padding-right: 0.5em;
        margin: 0.35em 0 0.35em 0.35em;
    }

    .flatworm-nav .left>a::before {
        content: "«";
        left: -0.75em;
        transition: left 1s ease-in-out;
        -webkit-animation: bounce-left 1.7s infinite;
        animation: bounce-left 1.7s infinite;
        -webkit-animation-play-state: paused;
        animation-play-state: paused;
    }

    .flatworm-nav .left>a:hover::before {
        -webkit-animation-play-state: running;
        animation-play-state: running;
    }

    .flatworm-nav .footer-wikiwalk-nav .right {
        text-align: left;
        justify-content: flex-start;
        border-left: 0.0625rem solid rgb(var(--white-monochrome));
        padding-left: 0.5em;
        padding-right: 1em;
        margin: 0.35em 0.35em 0.35em 0;
    }

    .flatworm-nav .right>a::before {
        content: "»";
        right: -0.75em;
        transition: left 1s ease-in-out;
        -webkit-animation: bounce-right 1.7s infinite;
        animation: bounce-right 1.7s infinite;
        -webkit-animation-play-state: paused;
        animation-play-state: paused;
    }

    .flatworm-nav .right>a:hover::before {
        -webkit-animation-play-state: running;
        animation-play-state: running;
    }

@-webkit-keyframes bounce-left {
        0% {
            left: -0.75em;
        }

        50% {
            left: -0.50rem;
        }

        100% {
            left: -0.75em;
        }
    }

    @keyframes bounce-left {
        0% {
            left: -0.75em;
        }

        50% {
            left: -0.50rem;
        }

        100% {
            left: -0.75em;
        }
    }

    @-webkit-keyframes bounce-right {
        0% {
            right: -0.75em;
        }

        50% {
            right: -0.50rem;
        }

        100% {
            right: -0.75em;
        }
    }

    @keyframes bounce-right {
        0% {
            right: -0.75em;
        }

        50% {
            right: -0.50rem;
        }

        100% {
            right: -0.75em;
        }
    }
