[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Oswald:wght@200;300&display=swap');

.baseHeading {
    width: 100%;
    height: 150px;
    margin: auto;
    border: 1px solid #ccc;
    font-family: 'Oswald', sans-serif;
    background: white;
}

.leftBlockHeading {
    width: 29%;
    float: left;
    height: 100%;
}
.confidentialHeading {
    height: 25%;
    padding-left: 20px;
    padding-top: 15px;
}
.confidentialSpan {
    font-size: 36px;
    line-height: 0px;
}
.confidentialSpan a {
    color: #000;
}
.detailsHeading {
    height: 70%;
    margin: 0;
    padding-left: 20px;
}
.detailsSpan {
    font-size: auto;
}
.detailsSpan[data-title="none"] {
    display: none;
}

.centerBlockHeading {
    width: 55%;
    float: left;
    text-align: center;
    padding-top: 30px;
}

div#u-dangerLevels-1:hover::after, div#u-dangerLevels-2:hover::after, div#u-dangerLevels-3:hover::after, div#u-dangerLevels-4:hover::after, div#u-dangerLevels-5:hover::after, div#u-dangerLevels-6:hover::after {
    display: block;
    content: attr(data-title); /* Выводим текст */
    position: absolute; /* Абсолютное позиционирование */
    z-index: 1; /* Отображаем подсказку поверх других элементов */
    background: rgba(255,255,230,0.9); /* Полупрозрачный цвет фона */
    font-family: Arial, sans-serif; /* Гарнитура шрифта */
    font-size: 11px; /* Размер текста подсказки */
    padding: 5px 10px; /* Поля */
    border: 1px solid #333; /* Параметры рамки */
    width: 20em;
    top: auto;
    transform: skew(0deg) !important;
    color: #333;
}
div#u-dangerLevels-1[data-title="none"], div#u-dangerLevels-2[data-title="none"], div#u-dangerLevels-3[data-title="none"], div#u-dangerLevels-4[data-title="none"], div#u-dangerLevels-5[data-title="none"], div#u-dangerLevels-6[data-title="none"] {
    display: none !important;
}

.rightBlockHeading {
    width: 14%;
    height: 80%;
    float: left;
    text-align: center;
    position: relative;
}
.rightBlockHeading div {;
    display: none;
}
.{$scp-class} #classImageHeading-{$scp-class} {
    width: 70%;
    height: 80%;
    display: inline-block;
    margin: 7% 0px;
}
.{$scp-class} #u-classImageHeading-{$scp-class} {
    display: inline-block !important;
    width: 100px;
    height: 90px;
    margin-top: 15px;
}
.{$scp-class} #u-classImageHeading-{$scp-class}:hover::after {
    display: block;
    content: attr(data-title); /* Выводим текст */
    position: absolute; /* Относительное позиционирование */
    z-index: 3; /* Отображаем подсказку поверх других элементов */
    background: rgba(255,255,230,0.9); /* Полупрозрачный цвет фона */
    font-family: Arial, sans-serif; /* Гарнитура шрифта */
    font-size: 11px; /* Размер текста подсказки */
    padding: 5px 10px; /* Поля */
    border: 1px solid #333; /* Параметры рамки */
    transform: skew(0deg) !important;
    color: #333;
}

#u-classImageHeading-nonstandard:not([data-title="クラス: safe"]):not([data-title="クラス: euclid"]):not([data-title="クラス: keter"]):not([data-title="クラス: thaumiel"]):not([data-title="クラス: neutralized"]) {
    display: inline-block !important;
    width: 100px;
    height: 90px;
    margin-top: 15px;
}
#u-classImageHeading-nonstandard:not([data-title="クラス: safe"]):not([data-title="クラス: euclid"]):not([data-title="クラス: keter"]):not([data-title="クラス: thaumiel"]):not([data-title="クラス: neutralized"]):hover::after {
    display: block;
    content: attr(data-title); /* Выводим текст */
    position: absolute; /* Абсолютное позиционирование */
    left: 20%; /* top: 10%; */ /* Положение подсказки */
    z-index: 1; /* Отображаем подсказку поверх других элементов */
    background: rgba(255,255,230,0.9); /* Полупрозрачный цвет фона */
    font-family: Arial, sans-serif; /* Гарнитура шрифта */
    font-size: 11px; /* Размер текста подсказки */
    padding: 5px 10px; /* Поля */
    border: 1px solid #333; /* Параметры рамки */
    transform: skew(0deg) !important;
    color: #333;
}


.bottomBlockHeading {
    width: 70%;
    height: 20%;
    text-align: center;
    display: inline-flex;
    float: right;
}
.componentHeading {
    font-size: 18px;
    width: 55%;
    text-align: center;
    background: #ccc;
    height: 100%;
    transform: skew(-45deg);
    box-shadow: inset 5px 5px 4px -4px rgba(0,0,0,0.7);
}
.componentHeadingSkewFixSpan {
  transform: skew(45deg);
  display: block;
}

.xxxxHeading {
    font-size: 18px;
    width: 15%;
    text-align: center;
    background: #ccc;
    height: 100%;
    transform: skew(-45deg);
    box-shadow: inset 0 5px 4px -4px rgba(0,0,0,0.7);
}
.contaiment-risk-{$contaiment-risk} #u-xxxxHeading-{$contaiment-risk} {
    display: flex !important;
    text-align: center;
    width: 17.1%;
    transform: skew(-45deg);
    z-index: 1;
    box-shadow: inset 0 5px 4px -4px rgba(0,0,0,0.7);
}
.xxxxSkewFixSpan {
  transform: skew(45deg);
  display: block;
}
.XXXXheadBox {
  color: black;
  position: relative;
  top: -15px;
  margin: 0 0 0 20%;
  transform: skew(45deg);
}
.contaiment-risk-{$contaiment-risk} #u-xxxxHeading-{$contaiment-risk}[data-title]:hover::after {
    display: block;
    content: attr(data-title); /* Выводим текст */
    position: absolute; /* Абсолютное позиционирование */
    z-index: 1; /* Отображаем подсказку поверх других элементов */
    background: rgba(255,255,230,0.9); /* Полупрозрачный цвет фона */
    font-family: Arial, sans-serif; /* Гарнитура шрифта */
    font-size: 11px; /* Размер текста подсказки */
    padding: 5px 10px; /* Поля */
    border: 1px solid #333; /* Параметры рамки */
    transform: skew(45deg);
    width: max-content;
    top: 110%;
    color: #333;
}

.threat-ind-{$threat-ind} #u-potentialLevel-{$threat-ind} {
    font-size: 18px;
    width: 14%;
    text-align: center;
    background: #ccc;
    height: 100%;
    display: none;
    color: white;
    box-shadow: inset 0 5px 4px -4px rgba(0,0,0,0.7);
}
.potentialLvl-Bl {
    color: black;
    position: relative;
    top: -15px;
    margin: 0 0 0 10%;
    transform: skew(45deg);
}
.potentialLvl-Wh {
    color: white;
    position: relative;
    top: -15px;
    margin: 0 0 0 10%;
    transform: skew(45deg);
}
.threat-ind-{$threat-ind} #u-potentialLevel-{$threat-ind} {
    display: flex !important;
    text-align: center;
    width: 18%;
    transform: skew(-45deg);
    z-index: 1;
    box-shadow: inset 0 5px 4px -4px rgba(0,0,0,0.7);
}
.threat-ind-{$threat-ind} #u-potentialLevel-{$threat-ind}:hover::after {
    display: block;
    content: attr(data-title); /* Выводим текст */
    position: absolute; /* Абсолютное позиционирование */
    z-index: 1; /* Отображаем подсказку поверх других элементов */
    background: rgba(255,255,230,0.9); /* Полупрозрачный цвет фона */
    font-family: Arial, sans-serif; /* Гарнитура шрифта */
    font-size: 11px; /* Размер текста подсказки */
    padding: 5px 10px; /* Поля */
    border: 1px solid #333; /* Параметры рамки */
    transform: skew(45deg);
    width: max-content;
    top: 110%;
    color: #333;
}

.numberHeading {
    font-size: 18px;
    width: 30%;
    text-align: center;
    background: #ccc;
    margin-left: -15px;
    box-shadow: inset 0 5px 4px -4px rgba(0,0,0,0.7);
    margin-top: -0.2px;
}

/* Оптимизация для мобилок by MrNereof */
@media (max-width: 1024px) {
    div#u-dangerLevels-1, div#u-dangerLevels-2, div#u-dangerLevels-3, div#u-dangerLevels-4, div#u-dangerLevels-5, div#u-dangerLevels-6 {
        width: 60px !important;
        height: 60px !important;
        background-size: 60px !important;
        margin-top: 20px !important;
    }

    .{$scp-class} div#u-classImageHeading-{$scp-class}, div#u-classImageHeading-nonstandard {
        width: 90px !important;
        height: 90px !important;
    }

    .{$scp-class} div#u-classImageHeading-{$scp-class} span, div#u-classImageHeading-nonstandard span {
        line-height: 50px !important;
    }

    span.confidentialSpan {
        font-size: 29px !important;
    }

    .bottomBlockHeading {
        width: 30em !important;
    }

    .componentHeading {
        transform: skew(0deg) !important;
    }

    .componentHeadingSkewFixSpan {
        transform: skew(0deg) !important;
    }

    .contaiment-risk-{$contaiment-risk} #u-xxxxHeading-{$contaiment-risk} {
        width: 3em !important;
        transform: skew(0deg) !important;
    }

    .XXXXheadBox {
        transform: skew(0deg) !important;
        margin: 0 auto;
    }

    .threat-ind-{$threat-ind} #u-potentialLevel-{$threat-ind} {
        width: 5em !important;
        transform: skew(0deg) !important;
    }

    .potentialLvl-Wh {
        transform: skew(0deg) !important;
    }
    .numberHeading {
        width: 10em !important;
    }
    .contaiment-risk-{$contaiment-risk} #u-xxxxHeading-{$contaiment-risk}[data-title]::after {
        transform: skew(0deg) !important;
        width: 20em !important;
        color: #333;
    }
    .threat-ind-{$threat-ind} #u-potentialLevel-{$threat-ind}::after {
        transform: skew(0deg) !important;
        width: 20em !important;
    }
}

@media (max-width: 970px) {
    .baseHeading {
        padding-bottom: 105px;
    }

    .detailsHeading {
        max-width: 200% !important;
        width: 200%;
        font-size: 1.2em;
    }

    .centerBlockHeading {
        display: flex;
        width: 100%;
        margin-left: 10px;
        padding-top: 15px;
    }

    div#u-dangerLevels-1, div#u-dangerLevels-2, div#u-dangerLevels-3, div#u-dangerLevels-4, div#u-dangerLevels-5, div#u-dangerLevels-6 {
        margin-top: -10px !important;
    }

     .rightBlockHeading {
        position: absolute;
        right: 10%;
        width: 90px !important;
        height: 90px !important;
        margin-top: 25px;
    }

    .bottomBlockHeading {
        margin-top: 10px;
        width: 100% !important;
    }
}

@media (max-width: 410px) {
    div#u-dangerLevels-1, div#u-dangerLevels-2, div#u-dangerLevels-3, div#u-dangerLevels-4, div#u-dangerLevels-5, div#u-dangerLevels-6 {
        width: 50px !important;
        height: 50px !important;
        background-size: 50px !important;
    }

    .baseHeading {
        padding-bottom: 95px;
    }
}
