[[code type="css"]]
@charset "utf-8";
/*版头*/
#header {
    background-image:url(https://scp-wiki-cn.wikidot.com/local--files/component:sea-level-plan/%E7%99%BD%E8%89%B2%E4%B8%89%E8%A7%92.png);
    background-size:105px;
}

div#container-wrap {
    background-image: url(https://scp-wiki-cn.wikidot.com/local--files/component:sea-level-plan/21ED472E224F95B3E74381FDE866FBCA.jpg);
    background-repeat: repeat-x;
}


#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: #eee;
    background: transparent;
    font-family: BauhausLTDemi, Simhei, Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    letter-spacing: 0.9px;
    text-shadow: none;
}
#header h1 a::before {
    content: "海平面计划";
    margin: 0 10px;
    font-size: 105%;
    font-family: 微软雅黑,BauhausLTDemi, Simhei, Arial, sans-serif;
    color: #fff;
    text-shadow: none;
}
#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
    color:#fff;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
    color:trtransparent;
}
#header h2 span {
    display:block;
    margin:0 15px;
    padding: 15px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color:transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "守护异世的门宅";
    display: block;
    padding: 5px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #FFF;
    font-size: 125%;
    font-family: 微软雅黑,BauhausLTDemi, Simhei, Arial, sans-serif;
}
/*个人简介*/
.owindow {
    background-color: white;
    border: 2px solid #483d8b;
    text-align: left;
    position: absolute;
    color: #483d8b;
}
a.btn.btn-primary {
    color: #483d8b;
}
a.btn.btn-danger.btn-small.btn-sm {
    color: #483d9e;
}
.owindow .button-bar a {
    margin: 0.2em 0.2em;
    padding: 0.1em 1em;
    text-decoration: none;
    background-color: #F4F4F4;
    border: 1px solid #483d9e;
    color: #483d9e;
}
/*个人用户*/
#login-status a {
    background: transparent;
    color: #ddd;
    text-decoration: none;
}
#login-status ul a {
    color: #483D8B;
    background: transparent;
    transition: 1s all;
}
#account-options li {
    display: block;
    margin: 0;
    padding: 0;
    border: solid 1px #483d8b;
    transition: 0.5s all;
}
#account-options {
    position: absolute;
    -moz-box-sizing: border-box;
    width: 15em;
    right: 0;
    padding: 0;
    z-index: 40;
    background-color: white;
    border: 1px solid #483d8b;
}
#login-status ul a:hover {
    color: #f3f3f3;
    background: #483D8B;
}
/*顶栏*/
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    color: #483D8B;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}
#top-bar ul li ul a, #top-bar a:hover {
    color: rgba(0,0,0,0);
    background: rgba(0,0,0,0);
}
#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
    transition: weight 2s,height 2s,background 1s,color 1s;
}
#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
    background: #483d8b;
    text-decoration: none;
    color: #f3f3f3;
}
#top-bar ul li ul {
    border: solid 1px #483d8b;
    box-shadow: 0 2px 6px #d9ecf9;
    border-top: 0;
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #483d8b;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}
/*搜索栏*/
#search-top-box-form input[type="submit"] {
    border: solid 1px #fff;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #fff;
    background: transparent;
    box-shadow: none;
    cursor: pointer;
}
#search-top-box-input {
    border: solid 1px #fff;
    border-radius: 5px;
    color: #fff;
    background-color: transparent;
    box-shadow: none;
}
#search-top-box-input:hover, #search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: transparent;
    box-shadow: none;
}
#search-top-box input.empty {
    color: #fff;
}
#search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25);
    background: transparent;
    box-shadow: none;
}
/*文章部分*/
h1, #page-title {
    color: #483D8B;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
#page-content {
    color: #483d8b;
}
#page-title {
    border-color: #483d8b;
}
/*侧栏*/
#side-bar .side-block {
    padding: 10px;
    border: 1px solid #483D8B;
    border-radius: 0px;
    box-shadow: none;
    background-image: linear-gradient(0deg,#fff,#fff);
    margin-bottom: 15px;
    transition: 1s all;
}
#side-bar .side-block:hover {
    padding: 10px;
    border: 1px solid #660000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(102,0,0,.5);
    background: #fff;
    margin-bottom: 15px;
}
#side-bar div.menu-item a {
    font-weight: bold;
    color: #483D8B;
    transition: 1s all;
    text-decoration: none;
}
#side-bar div.menu-item a:hover {
    color: #b01;
}
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
    color: #483D8B;
    transition: 1s all;
    border: none;
    background: none;
}
#side-bar .collapsible-block-link:hover {
    color: #b01;
    text-decoration: none;
}
#side-bar .heading {
    color: #483D8B;
    font-size: 8pt;
    font-weight: bold;
    border-bottom: solid 1px #483d8b;
}
hr {
    margin: 1em 2em;
    padding: 0;
    height: 1px;
    border: none;
    color: #DDD;
    background-color: #483d8b;
}
#side-bar img, #side-bar .collapsible-block-folded {
    -webkit-filter: hue-rotate(264.2deg) grayscale(42%);
    filter: hue-rotate(264.2deg) grayscale(42%);
}
/*超链接*/
a {
    color: #ac0011;
}
a:visited {
    color: #ac0011;
}
/*折叠块*/
.collapsible-block-link {
    border: solid 2px #ac0011;
    background: #f3f3f3;
    color: #ac0011;
    transition: 0.5s all;
    border-radius: 3px;
}
.collapsible-block-link:hover {
    color: #f3f3f3;
    background: #483D8B;
    text-decoration: none;
    border: solid 2px #483D8B;
}
/*tab格*/
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    background: #483D8B;
    color: #fff;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background-color: #fff;
}
/*评分*/
.page-rate-widget-box .rate-points {
    background-color: #483D8B !important;
    border: solid 1px #483D8B;
    border-right: 0;
    border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .cancel {
    background-color: #483D8B;
    border: solid 1px #483D8B;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #fff;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #483D8B;
    color: #fff;
    text-decoration: none;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #fff;
    border-top: solid 1px #483D8B;
    border-bottom: solid 1px #483D8B;
    font-weight: bold;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    background: transparent;
    color: #483D8B;
    padding: 0 4px;
    margin: 0 1px;
    transition: background .5s,color .5s;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background: #483D8B;
    color: #fff;
    text-decoration: none;
}
/*引用块*/
blockquote {
    border: 2px dashed #483d8b;
    padding: 0 1em;
    color: #483d8b;
    --sea-color: rgb(214, 234, 248);
    background: linear-gradient(90deg, rgba(255,255,255,0) 0%, var(--sea-color) 20%, var(--sea-color) 80%, rgba(255,255,255,0) 100%);
}
/*源代码框*/
.page-source {
    border: 2px dashed #483d8b;
    padding: 1em 2em;
    color: #483d8b;
}
/*底部编辑按钮*/
.page-options-bottom a {
    margin: 2px 2px;
    font-size: 95%;
    padding: 0 8px;
    border: dashed 2px #483D8B;
    background: rgb(214, 234, 248);
    transition: 1s all;
    text-decoration: none;
    color: #483D8B;
}
.page-options-bottom a:hover {
    color: #ac0011;
    text-decoration: none;
    background: transparent;
    border: dashed 2px #ac0011;
}
a.action-area-close {
    float: right;
    margin: 2px 2px;
    font-size: 95%;
    padding: 0 0.7em;
    background: #d6eaf8;
    border: dashed 2px;
    text-decoration: none;
    transition: 1s all;
    color: #483d8b;
}
a.action-area-close:hover {
    color: #ac0011;
    text-decoration: none;
    background: transparent;
    border: dashed 2px #ac0011;
}
/*tag*/
.page-tags span {
    border-top: 2px solid #483d8b;
    font-size: 85%;
}
#main-content .page-tags a {
    display: inline-block;
    white-space: nowrap;
    text-decoration: none;
}
/*tab格子*/
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    font-family: 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
    background: #483d8b;
        background-size: auto;
    color: rgb(255,255,255);
    background-size: cover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 0px;
    border-top: none;
    border-left: 1px solid #483d8b;
    border-right: solid 1px #483d8b;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    font-family: 'Palatino Linotype', 'Book Antiqua', 'SimSun Regular', Palatino, serif;
    background-color: rgb(255,255,255);
    background-size: cover;
    border-width: 0px;
        border-top-width: 0px;
        border-right-width: 0px;
        border-left-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 0px;
    border-top: solid 1px #483d8b;
    border-left: solid 1px #483d8b;
    border-right: solid 1px #483d8b;
    transition: 0.5s all cubic-bezier(.65,.05,.36,1);
    color: #483d8b;
}
.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus {
    font-family: 'Palatino Linotype', 'Book Antiqua', 'SimSun Regular', Palatino, serif;
    color: rgb(255,255,255);
    background: #483d8b;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
    border: none;
    border-width: 1px 0 0;
    padding: 0.25em .75em;
    left: 0;
    right: 0;
    bottom: 0;
    top: -1px;
    position: relative;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-color: #483d8b;
}
.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    border: 1px solid #483d8b;
    padding: 0.25em 0.5em;
}
.yui-navset .yui-content {
    background-color: rgb(255,255,255);
}
/*code*/
.code {
    border: 2px dashed #483d8b;
    font-family: 'Andale Mono', 'Courier New', Courier, monospace;
    padding: 0 1em;
    margin: 0.4em 0;
    overflow: auto;
    --sea-color: rgb(214, 234, 248);
    background: linear-gradient(30deg, rgba(255,255,255,0) 0%, var(--sea-color) 20%, var(--sea-color) 80%, rgba(255,255,255,0) 100%);
}
/*note*/
div.wiki-note {
    width: auto;
    margin: 0.5em 5em;
    border: 2px solid #483d8b;
    text-align: center;
}
/*脚注*/
.footnotes-footer {
    margin: 0;
    padding: 0.5em 0;
    border-left: 0.75rem solid #483d8b;
    height: 1%;
    --sea-color: rgb(214, 234, 248);
    background: repeating-linear-gradient(60deg, rgba(255,255,255,0) 0%, var(--sea-color) 20%, var(--sea-color) 80%, rgba(255,255,255,0) 100%);
}
/*编辑次数*/
#page-info {
    clear: both;
    text-align: right;
    font-size: 87%;
    margin: 2px 2px;
    color: #483d8b;
}
/*历史记录*/
div.buttons input, input.button, button, file, a.button {
    margin: 0 2px;
    padding: 0px 0.5em;
    border: dashed 2px #483D8B;
    background: rgb(214, 234, 248);
    transition: 1s all;
    text-decoration: none;
    color: #483D8B;
}
div.buttons input:hover, input.button:hover, button:hover, a.button:hover {
    color: #ac0011;
    text-decoration: none;
    background: transparent;
    border: dashed 2px #ac0011;
}
table.page-history td.optionstd a {
    padding: 0 3px;
    text-decoration: none;
    border: dashed 1px #483D8B;
    background: rgb(214, 234, 248);
    transition: 1s all;
    color: #483D8B;
}
table.page-history td.optionstd a:hover {
    color: #ac0011;
    text-decoration: none;
    background: transparent;
    border: dashed 1px #ac0011;
}
.pager .current {
    padding: 0 7px;
    margin: 0 3px;
    font-weight: bold;
    background-color: #ac0011;
    color: #FFF;
    border: 1px solid #ac0011;
}
.pager a {
    padding: 0 7px;
    margin: 0 3px;
    border: 1px solid #483d8b;
    text-decoration: none;
    background: #483d8b;
    color: #fff;
    transition: 0.5s all;
}
.pager a:hover {
    border: 1px solid #ac0011;
    text-decoration: none;
    background: #ac0011;
    color: #fff;
}
/*提示栏*/
.hovertip {
    display: none;
    width: 20em;
    border: 2px solid #483d8b !important;
    background-color: #f3f3f3;
    z-index: 100;
    color: #483d8b;
}
/*SCP图片*/
.scp-image-block {
    border: solid 1px #483d8b;
    box-shadow: 0 1px 6px #d6eaf8;
}
.scp-image-block .scp-image-caption {
    background-color: #483d8b;
    border-top: solid 1px #483d8b;
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    color: #fff;
}
.scp-image-block img {
    border: 0;
    transition: 1s all;
}
.scp-image-block img:hover {
    transform: scale(1.1);
}
/*编辑器*/
textarea {
    font-family: verdana,arial,helvetica,sans-serif;
    padding: 2px;
    font-size: 100%;
    border: 1px solid #483d8b;
    background-color: #483d8b;
    color: #fff;
    transition: 1s all;
}
textarea:hover {
    background: #901;
    border: solid 1px #901;
}
#lock-info {
    background-color: #483d8b;
    border: 1px solid #483d8b;
    padding: 0.5em 1em;
    margin: 1em 0;
    color: #fff;
}
input.text {
    border: 1px solid #483d8b;
    margin: 0 1px;
    padding: 1px 2px;
    background-color: #483d8b;
    color: #fff;
    transition: 1s all;
}
input.text:hover {
    background: #901;
    border: solid 1px #901;
    color: #fff;
}
.change-textarea-size a {
    color: #fff;
    background-color: #483d8b;
    border: 1px solid #483d8b;
    border-width: 0 1px 1px 1px;
    padding: 2px 4px;
    font-size: 120%;
    text-decoration: none;
    transition: 1s all;
}
.change-textarea-size a:hover {
    color: #fff;
    background: #901;
    border: 1px solid #901;
}
table.form td div.sub, form div.sub {
    font-size: 90%;
    color: #483d8b;
}
/*body*/
body {
    font-size: 0.80em;
    color: #483d8b;
}
/*底栏*/
#license-area {
    padding: 0.5em 0;
    color: #483d8b;
    text-align: center;
}
/*tag*/
#main-content .page-tags a {
    display: inline-block;
    white-space: nowrap;
    text-decoration: none;
    color: #483d8b;
}
/*停止关注*/
.page-watch-options a {
    text-decoration: none;
    color: #483d8b;
}
/*列表*/
table.wiki-content-table th {
    border: 1px solid #483d8b;
    padding: 0.3em 0.7em;
    background-color: #483d8b;
    color: #fff;
}
table.wiki-content-table td {
    border: 1px solid #483d8b;
    padding: 0.3em 0.7em;
}
/*评分*/
.printuser a {
    margin-right: -1px;
    color: #483d8b !important;
}
/*小惊喜*/
.unmargined, .unmargined + hr {
    filter: hue-rotate(264.2deg) grayscale(50%);
}
/*网页适应性*/
@media (max-width: 497px) {
.open-menu a {
    position: fixed;
    top: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: 3px solid #fff !important;
    background-color: transparent !important;
    border-radius: 5px;
    color: #fff !important;
    box-shadow: 0px 0px 20px 3px #483d8b;
    transition: 0.5s all;
}
.open-menu a:hover {
    text-decoration: none !important;
    box-shadow: 0px 0px 20px 3px #d6eaf8;
}
#header {
    background-position: 0 3em;
}
#header h1 a::before {
    content: "海平面计划";
    margin: 0 30px;
    font-size: 48%;
    font-family: 微软雅黑,BauhausLTDemi, Simhei, Arial, sans-serif;
    color: #fff;
    text-shadow: none;
}
#header h2 span {
    display: block;
    margin: 0 0px;
    padding: 15px 0px;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "守护异世的门宅";
    display: block;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #FFF;
    font-size: 90%;
    font-family: 微软雅黑,BauhausLTDemi, Simhei, Arial, sans-serif;
    position: absolute;
    bottom: 60px;
    left: 100px;
}
}
[[/code]]

[[/collapsible]]

[[/iftags]]

