[[module CSS]]
@import url(https://scp-wiki.wdfiles.com/local--code/component:slim-acs-source/1);
[[/module]]

[!-- {$inc-animation}]
[[div class="sacs-v4_animation"]]
[!{$inc-animation} --]

[!-- {$inc-darkmode}]
[[div class="sacs-v4_darkmode"]]
[!{$inc-darkmode} --]

[!-- {$inc-wide}]
[[div class="sacs-v4_wide"]]
[!{$inc-wide} --]

[[div class="sacs_v4 sacs-v4_attribute-{$clearance}"]]

  [[div class="sacs-v4_top"]]
    [[div class="sacs-v4_flex sacs-v4_item"]]
      [!-- {$inc-no-item}][[span class="sacs-v4_no-display"]][!{$inc-no-item} --]Item #:[!-- {$inc-no-item}][[/span]][!{$inc-no-item} --] [[span class="sacs-v4_big"]]{$item-number}[[/span]]⠀
    [[/div]]

    [[div class="sacs-v4_bars"]]
      [[div class="sacs-v4_bar-six"]]
      [[/div]]
      [[div class="sacs-v4_bar-five"]]
      [[/div]]
      [[div class="sacs-v4_bar-four"]]
      [[/div]]
      [[div class="sacs-v4_bar-three"]]
      [[/div]]
      [[div class="sacs-v4_bar-two"]]
      [[/div]]
      [[div class="sacs-v4_bar-one"]]
      [[/div]]
    [[/div]]

    [[div class="sacs-v4_flex sacs-v4_clearance"]]
      [[span class="sacs-v4_medium"]]Level {$clearance}[[/span]]
      [[span class="sacs-v4_clearance-text"]]⠀[[/span]]
    [[/div]]
  [[/div]]

  [[div class="sacs-v4_separator"]]
  [[/div]]

  [[div class="sacs-v4_grid"]]

    [[div class="sacs-v4_containment {$container-class}"]]
      Object Class:
      [[span class="sacs-v4_big sacs-v4_high-sat"]]{$container-class}[[/span]]
    [[/div]]
    [[div class="sacs-v4_disruption {$disruption-class}"]]
      Disruption Class: [[span class="sacs-v4_high-sat"]]{$disruption-class}[[/span]]
    [[/div]]
    [[div class="sacs-v4_risk {$risk-class}"]]
      Risk Class: [[span class="sacs-v4_high-sat"]]{$risk-class}[[/span]]
    [[/div]]

  [[/div]]

[[/div]]

[!-- {$inc-wide}]
[[/div]]
[!{$inc-wide} --]

[!-- {$inc-darkmode}]
[[/div]]
[!{$inc-darkmode} --]

[!-- {$inc-animation}]
[[/div]]
[!{$inc-animation} --]


