[[module CSS]]
.tooltip {
  position: relative;
  display: inline-block;
  transition: transform .16s;
}
:where(#page-content) .tooltip > a[href*="dark-achievement-badges"] {
  display: block;
  box-shadow: none!important;
  background: transparent;
}
.tooltip .tooltiptext {
  width: 120px;
  background-color: #21252E;
  color: #f8f8f8;
  text-align: center;
  padding: 5px;
  border-radius: 6px;
  position: absolute;
  top: 95px;
  left: calc(50% - 5px);
  margin-left: -60px;
  z-index: 2;
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.3s;
}
.tooltip .tooltiptext::after {
    content: "";
    position: absolute;
    bottom: 100%;
    left: 50%;
    margin-left: -5px;
    border-width: 5px;
    border-style: solid;
    border-color: transparent transparent #21252E transparent;
}
.tooltip:is(:hover,:focus-within) {
    transform: scale(1.6);
    z-index: 2;
}
.tooltip:is(:hover,:focus-within) .tooltiptext {
  visibility: visible;
  opacity: 1;
}

 /* specific codetext */
.phd::after {
  content: "占位符";
}
