content:
[[module css]]
.tagmodal {
  float: right;
  margin-bottom: 10px;
  margin-right: 2em;
}

.tagmodal .folded .colmod-content {
  display: none;
}

.tagmodal .folded .modal-close {
  opacity: 0;
  pointer-events: none;
}

.tagmodal .modal-close {
  top: 0;
  left: 0;
  position: fixed;
  display: flex;
  display: -webkit-flex;
  z-index: 10;
}

.tagmodal .modalcontainer {
  position: absolute;
  top: 25vh;
  left: max(calc((100vw - 600px)/2), 2vw);
  bottom: unset;
  right: unset;
  width: 600px;
  max-width: 95vw !important;
  height: 350px;
  margin: auto;
  pointer-events: auto !important;
  transform: translateY(0);
}

.tagmodal .modalbox {
  position: relative;
  background: #fefefe;
  background: linear-gradient(#eeeeee 51px, #fefefe 51px, #fefefe);
  border: #333333 solid 1px;
  border-radius: 5px;
  box-shadow: 0 2px 6px rgb(102 0 0 / 50%);
  height: 50vh;
  pointer-events: auto !important;
}

.foldable-list-container.shadow > a {
  position: fixed;
  top: 0;
  left: 0;
  z-index: -1;
  width: 100%;
  height: 100%;
  background-color: #000000;
  opacity: 0;
}

.tagmodal .unfolded .foldable-list-container.shadow > a {
  opacity: 0.6;
  transition: left 0s 0.1s, opacity 0.5s ease-in-out 0.1s;
  -webkit-transition: left 0s 0.1s, opacity 0.5s ease-in-out 0.1s;
}

/*  */

.tagmodal .foldable-list-container.tagButton {
  display: inline-flex;
  justify-content: center;
  align-items: center;
  width: 24px;
  height: 16px;
  background-color: #663333;
  border: 1px solid #663333;
  border-radius: 5px;
  box-shadow: 1px 1px 3px rgb(0 0 0 / 50%);
  margin-left: 5px;
}

.tagmodal .tagButton > a {
  width: 24px;
  margin-left: 0;
  color: #fff6f0;
  text-align: center;
  text-decoration: none;
  font: normal normal normal 11px/1 FontAwesome;
  text-rendering: auto;
  transform: translate(0, 0);
  -webkit-font-smoothing: antialiased;
  font-size: 0;
}

.tagmodal > ul > li.unfolded > .colmod-link-top a:first-child {
  display: initial;
}

.tagmodal .tagButton > a::before {
  content: "\f02c";
  font: normal normal normal 11px/1 FontAwesome;
  text-rendering: auto;
  transform: translate(0, 0);
  -webkit-font-smoothing: antialiased;
}

/*  */

.modalbox .modal-head {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  min-height: 50px;
  background: #cccccc;
  margin-bottom: 14px;
  border-radius: 5px 5px 0 0;
}

.modalbox .modal-body {
  margin: 2em;
}

.modalbox .modal-body .tagsfoot {
  bottom: 0;
  left: 0;
  position: absolute;
  padding: 2em;
  background: #cccccc;
  border-bottom-right-radius: 5px;
  border-bottom-left-radius: 5px;
}

/*  */

.tagsdetails .error-block::after {
  content: "タグモーダルをListPagesモジュール内で使用することはできません．";
  font-size: 12.8px;
  padding: 12.8px;
  margin: 12.8px;
  border: 1px solid #ffaaaa;
  display: flex;
}

.tagsdetails .error-block {
  font-size: 0;
  padding: 0;
  margin: 0;
  border: none;
}

.tagmodal .tagsdetails ._t,
.tagmodal .tagsdetails ._h {
  display: none;
}

#main-content .tagmodal .tagsdetails .page-tags span {
  display: inline-flex;
}

#main-content .tagmodal .tagsdetails .page-tags a[href ^= "/system:page-tags/tag/_"] {
  display: inline-block;
}
[[/module]]

