[[module CSS show="true"]]
/* Basic message code */
.text-container .text {
  border-radius: 18px;
  text-align: left;
  display:inline-block;
  padding:8px 12px;
  font-weight:500;
  margin:1.5px 10px;
  max-width: 300px;
}

/* Container for "received" messages */
.recv {
  text-align: left;
  margin-right:20px;
}

/* Container for "sent" messages */
.sent {
  text-align: right;
  margin-left:20px;
}
.sent .text {
  border-bottom-right-radius: 3px;
  background-color: #267AE9;
  color: white;
}
.recv .text {
  border-bottom-left-radius: 3px;
  background-color: #dadada;
  color: black;
}

/* Fancy stuff on the corners of messages with another message from same person above them */
.sent .text:not(:first-child) {
  border-top-right-radius: 4px;
}
.recv .text:not(:first-child) {
  border-top-left-radius: 4px;
}

.text-container p {
  margin:0 0 15px;
}
/* Message area stuff */
/* Header styling */
.text-container-wrap h1, .text-container-wrap h2, .text-container-wrap h3, .text-container-wrap h4, .text-container-wrap h5, .text-container-wrap h6 {
  color: black;
  font-weight: bold;
  margin:0 auto 10px;
  position:relative;
  top:-8px;
}

/* External box */
.text-container-wrap {
  text-align:center;
  margin:auto;
  display:block;
  margin-top: 25px;
  width:400px;
  padding-top: 25px;
  border-radius:10px;
  border:3px solid #666;
  background-color:#fff;
  overflow:none;
  box-sizing:border-box;
  pointer-events:none!important;
  position:relative;
}

/* Make it so it looks like a user can't highlight the text (even though they can), so it feels more believable */
.text-container-wrap *::selection {
  background-color:transparent!important;
  color:inherit!important;
}

/* Internal box */
.text-container {
  box-shadow:1px 1px 7px #aaa inset;
  margin:0px auto 25px;
  width:350px;
  display:block;
  padding:25px 0px 6px;
  background-color:#f7f7f7;
}
