content:
[[module css]]
@import url(http://scp-jp.wdfiles.com/local--code/component%3Atheme-saint-patrick/1);
[[/module]]
[[code type="css"]]
@import url(http://fondationscp.wikidot.com/component:theme-saint-patrick/code/1);
#main-content {
    margin: 0 4em 0 20em;
    padding: 1em;
    position: relative;
}
#content-wrap {
position: relative;
margin: 2em auto 0;
max-width:920px;
width:100%;
}

#main-content {
margin: 0 4em 0 20em;
padding: 0em;
position: relative;
}

@media (max-width: 479px) {
#page-content {
font-size: 0.9em;
}

#main-content {
margin: 0;
}
}

@media (max-width: 767px) {

.page-history tbody tr td:last-child {
width: 35%;
}

.owindow {
min-width: 80%;
max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
float: left;
}

.owindow .button-bar {
float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
display: none;
}

.open-menu a {
position: fixed;
bottom: 0.5em;
left: 0.5em;
z-index: 15;
font-family: 'Nanum Gothic', san-serif;
font-size: 30px;
font-weight: 700;
width: 30px;
height: 30px;
line-height: 0.9em;
text-align: center;
border: 0.2em solid #888 !important;
background-color: #fff !important;
border-radius: 3em;
color: #888 !important;
}

.open-menu a:hover {
text-decoration: none !important;
-webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
-moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
-ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
-o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
}

#main-content {
max-width: 90%;
margin: 0 5%;
padding: 0;
-webkit-transition: 0.5s ease-in-out 0.1s;
-moz-transition: 0.5s ease-in-out 0.1s;
-ms-transition: 0.5s ease-in-out 0.1s;
-o-transition: 0.5s ease-in-out 0.1s;
transition: 0.5s ease-in-out 0.1s;
}

.cancel .btn .btn-default{
  background-color: #006c00;

}

#side-bar {
display: block;
position: fixed;
top: 0;
left: -25em;
width: 17em;
height: 100%;
  background-color: #006c00;
overflow-y: auto;
z-index: 10;
padding: 1em 1em 0 1em;
-webkit-transition: left 0.5s ease-in-out 0.1s;
-moz-transition: left 0.5s ease-in-out 0.1s;
-ms-transition: left 0.5s ease-in-out 0.1s;
-o-transition: left 0.5s ease-in-out 0.1s;
transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
content: "";
position: absolute;
top: 0;
width: 0;
height: 100%;
background-color: rgba(0, 0, 0, 0.2);

}

#side-bar:target {
display: block;
left: 0;
width: 17em;
margin: 0;
border: 1px solid #dedede;
z-index: 10;
}

#side-bar:target + #main-content {
left: 0;
}

#side-bar:target .close-menu {
display: block;
position: fixed;
width: 100%;
height: 100%;
top: 0;
left: 0;
background: rgba(0,0,0,0.3) 1px 1px repeat;
z-index: -1;
}
}

.page-rate-widget-box .cancel {
  background-color: #006c00;
  border: solid 1px #006c00;
  border-left: 0;
  border-radius: 0 5px 5px 0;
}

.page-rate-widget-box .cancel a {
  background-color: #006c00;
  text-transform: uppercase;
  color: #f0ffff;
}
[[/code]]
