/* off-canvas */
 .close-menu {
     display: none;
}
 @media (max-width: 767px) {
     .page-history tbody tr td:last-child {
         width: 35%;
    }
     .owindow {
         min-width: 80%;
         max-width: 99%;
    }
     .modal-body .table, .modal-body .table ~ div {
         float: left;
    }
     .owindow .button-bar {
         float: right;
    }
