         width: 100%;
    }
     .yui-navset {
         z-index: 1;
    }
     #navi-bar, #navi-bar-shadow {
         display: none;
    }
     #top-bar .open-menu a {
         position: fixed;
         bottom: 0.5em;
         left: 0.5em;
         z-index: 15;
         font-family: 'Nanum Gothic', san-serif;
         font-size: 30px;
         font-weight: 700;
         width: 30px;
         height: 30px;
         line-height: 0.9em;
         text-align: center;
         border: 0.2em solid #888;
         background-color: #fff;
         border-radius: 3em;
         color: #888;
    }
     #top-bar .open-menu a:hover {
         text-decoration: none !important;
         -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
         -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
         -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
         -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
         box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    }
     #main-content {
         max-width: 90%;
         margin: 0 5%;
         padding: 0;
         -webkit-transition: 0.5s ease-in-out 0.1s;
         -moz-transition: 0.5s ease-in-out 0.1s;
         -ms-transition: 0.5s ease-in-out 0.1s;
         -o-transition: 0.5s ease-in-out 0.1s;
         transition: 0.5s ease-in-out 0.1s;
    }
     #side-bar {
         display: block;
         position: fixed;
         top: 0;
         left: -25em;
         width: 17em;
         height: 100%;
         background-color: rgb(184, 134, 134);
         overflow-y: auto;
         z-index: 10;
         padding: 1em 1em 0 1em;
         -webkit-transition: left 0.5s ease-in-out 0.1s;
         -moz-transition: left 0.5s ease-in-out 0.1s;
         -ms-transition: left 0.5s ease-in-out 0.1s;
         -o-transition: left 0.5s ease-in-out 0.1s;
         transition: left 0.5s ease-in-out 0.1s;
    }
     #side-bar:after {
         content: "";
         position: absolute;
         top: 0;
         width: 0;
         height: 100%;
         background-color: rgba(0, 0, 0, 0.2);
    }
     #side-bar:target {
         display: block;
         left: 0;
         width: 17em;
         margin: 0;
         border: 1px solid #dedede;
         z-index: 10;
    }
     #side-bar:target + #main-content {
         left: 0;
    }
     #side-bar:target .close-menu {
         display: block;
         position: fixed;
         width: 100%;
         height: 100%;
         top: 0;
         left: 0;
         background: rgba(0,0,0,0.3) 1px 1px repeat;
         z-index: -1;
    }
