[[code type="css"]]
@charset "utf-8";

@import url('https://fonts.googleapis.com/css?family=Play|Open+Sans');

/*
    SCP-001 The Way It Ends
    [2018 Wikidot Theme]
    djkaktus
    Dr Devan
*/

/* COMMON */
h1, #page-title {
    color: #000000;
}

sup { 
    vertical-align: top; 
    position: relative; 
    top: -0.5em; 
} 

a {
    color: #e80e0e;
}
a.newpage {
    color: #e80e0e;
}
a:visited {
    color: #e80e0e;
}
.title.modal-header {
    background-color:#303030;
}
.owindow {
    background-color:#303030;
}

body {
    background-color: #000000;
    color: #dbdbdb;
}
div#container-wrap {
    background: url(http://www.kaktuskontainer.wikidot.com/local--files/component:thewayitendstheme/body_bg.png) top left repeat-x;
} 
/* HEADER */
#header {
    padding-bottom: 30px; /* FOR MENU */
    background: url(http://www.kaktuskontainer.wikidot.com/local--files/component:thewayitendstheme/logo.png) 10px 35px no-repeat;
}
#search-top-box-input {
    border: solid 1px #920909;
    color: #000000;
    background-color: #c20000;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #920909;
    color: #000000;
    background-color: #c20000;
}
#search-top-box-form input[type=submit] {
    border: solid 1px #920909;
    color: #000000;
    background-color: #c20000;
    background: linear-gradient(to bottom, #de0000,#9a0000,#610000);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #920909;
    color: #000000;
    text-shadow: 0 0 1px rgba(255,255,255,.25)
    background-color: #c20000;
    background: linear-gradient(to bottom, #de0000,#9a0000,#610000);
}

#login-status {
    color: #f4f4f4;
}
#login-status a {
    background-color: #000000;
    color: #f4f4f4;
}
#login-status ul a {
    color: #f4f4f4;
    background-color: #000000;
}

#account-topbutton {
    background: #920909;
    color: #f4f4f4;
}

#header h1 a {
    color: transparent;
    background: transparent;
    font-family: 'Play', sans-serif; 'Open Sans', sans-serif;
    text-shadow: none;
}

#header h1 a::before {
    content: "THE FOUNDATION";
    color: #c20000;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '観測者の事務室';
    color: #000000;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);}
}

/* TOP MENU */
#top-bar a {
    color: #C0C0C0;
}

#top-bar ul li ul {
    border: solid 1px #920909;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #C0C0C0;
    color: #e80e0e;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #C0C0C0;
}

/* SIDE MENU */
#side-bar {
    background: transparent;
}

#side-bar img {
    filter: hue-rotate(210deg);
    -webkit-filter: hue-rotate(210deg); brightness(120%); /* Safari 6.0 - 9.0 */
    filter: brightness(120%);
}

#side-bar div.side-block.sns img {
    filter: none;
    -webkit-filter: none;
}

#side-bar .side-block {
    border: 1px solid #920909;
    box-shadow: 0 2px 6px rgba(32,32,32,1) !important;
    background: #161616 !important;
}
#side-bar .heading {
    color: #dbdbdb;
    border-bottom: solid 1px #dbdbdb;
}
#side-bar div.menu-item.inactive a {
    color: #e80e0e;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #e80e0e;
}
#side-bar .collapsible-block-folded {
    background: none;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #e80e0e;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #e80e0e;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #b01;
}
#side-bar a:visited { 
    color: #e80e0e;
} 

/* YUI-TABS */
.yui-navset li {
    line-height: normal;
}

/* Custom page content classes */
.content-panel .panel-body {
    background: #C0C0C0;
}
.content-panel .panel-footer {
    color: #C0C0C0;
}
.content-panel .panel-footer a {
    color: #C0C0C0 ;
}
.content-panel .content-toc {
    background-color: #C0C0C0;
}

/* Page Rating Module Customizations */
.page-rate-widget-box {
    display: inline-block;
}
div.page-rate-widget-box .rate-points {
    background-color: #161616;
    border: solid 1px #920909;
    color: #e80e0e;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #161616;
    border-top: solid 1px #920909;
    border-bottom: solid 1px #920909;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background-color: #161616;
    color: #e80e0e;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #161616;
    color: #f4f4f4;
}
.page-rate-widget-box .cancel {
    background-color: #161616;
    border: solid 1px #920909;
}
.page-rate-widget-box .cancel a {
    color: #e80e0e;
}
.page-rate-widget-box .cancel a:hover {
    background-color: #161616;
    color: #f4f4f4;
}

/* Standard Image Block */
.scp-image-block {
    border: solid 1px #920909;
}
.scp-image-block .scp-image-caption {
    background-color: #C0C0C0;
    border-top: solid 1px #920909;
}

@media (max-width: 767px) {
.open-menu a {
    border: 0.2em solid #920909 !important;
    background-color: #C0C0C0 !important;
    color: #e80e0e !important;
}

#side-bar {
    background-color: rgb(192, 192, 192);
}
}

/* for the Interwiki */
#side-bar .scpnet-interwiki-wrapper iframe.scpnet-interwiki-frame {
    border: 1px solid #920909;
    box-shadow: 0 2px 6px rgba(32,32,32,1) !important;
    background-color: #fff;
    -webkit-filter: hue-rotate(185deg) saturate(90%) brightness(268%) invert(100%);
    filter: hue-rotate(185deg) saturate(90%) brightness(268%) invert(100%);
}
[[/code]]
[[/iftags]]
