content:
[[module css]]
.section {
    background: linear-gradient(to bottom, #eee 0%, #eee 50%, #fff 50%, #fff 100%);
    border: 1px solid #888;
    border-radius: 10px;
    overflow-x: auto;
    margin-bottom: 15px;}
.section > p {
    display: none;}
.section h3 {
    display: inline-block;
    float: left;
    padding-left: 0.7em;
    margin-top: none;
    margin-bottom: none;
    font-size: 1.4em;
    font-weight: bolder;
    color: #901;}
.section > a {
    float: left;
    font-size: 1.4em;
    font-weight: bolder;
    padding-right: 0.7em;
    margin-top: 0.5em;}
.section table {
    width: 100%;}
.section table * {
    text-align: left;}
.section table td, .section table th {
    border: none;}
.section table *:first-child {
    text-align: left;
    width: 100%;}
.section table td:first-child, .section table th:first-child {
    font-weight: bold;
    width: 110px;}
.section table td:nth-last-child(1), .section table th:nth-last-child(1) {
    word-break: keep-all;}
.section table tr{
    background: #fff;
    border-bottom: 1px dashed #888;}
.section table tr:first-child {
    border-bottom: 1px solid #888;}
.section table tr:last-child {
    border-bottom: none;}
.section table span,
.section table a {
    word-break: normal;
}

h2 {
    position: relative;
    left: 35px;
    padding-right: 40 px;
    z-index: -1;
}

.caption img {
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    margin-top: 15px;
    position: relative;
    top: 28px;
    z-index: -2;
}
[[/module]]
