[[module CSS]]
/* == OUTER CSS == */
 div.void {
    width: 23rem;
    margin: 1rem auto;
    padding: 0 .8rem;

    color: hsl(0, 0%, 5%);
    background: hsl(0, 0%, 100%);
    border: 1px solid lightgray;
    box-shadow: 3px 3px 0 1px hsla(0, 0%, 0%, .15);
}

 @media (max-width: 767px) {
     div.void {
     width: auto;
    }
}

/* == INNER CSS == */
 div.void span {
    word-break: unset;
}

 .vusermod {
    font-weight: bold;
}

 div.void ul {
    list-style-type: "ₒ৹๐ ";
}

/* == AVATAR == */
 .void-img {
    float: left;
    margin: 1rem 0 0 .4rem;
    padding: 0 1rem;
}

/* == VOID TRENDS == */
 .vactivity {
    color: blue;
    cursor: pointer;
}

 .vactivity span {
    word-break: unset;
}

 .vactivity:hover,
 .vactivity:focus {
    text-decoration: underline;
}
[[/module]]
