[[module CSS]]
/* General */

#page-content div.page-rate-widget-box {
    margin-right: 0;
}

#page-content div.wi-logo-header {
    width: 200px;
    height: 200px;
    margin: 32px auto;
    background-size: contain;
}

#page-content div.wi-title-block {
    margin: 32px 0;
    padding: 1em;
    background-color: #F0F0F0;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

#page-content div.wi-title-block p {
    margin: 0;
}

#page-content div.wi-title-text {
    flex-grow: 2;
    font-size: 1.5em;
    font-weight: bold;
    margin-right: 1em;
}

#page-content div.wi-title-link {
    flex-shrink: 0;
}

#page-content div.wi-title-block a {
    color: white;
    background-color: #990011;
    border-radius: 4px;
    margin-left: 4px;
    padding: 8px;
    font-weight: bold;
}

#page-content div.wi-participants-row {
    display: flex;
    margin: 24px 0;
}

#page-content div.wi-participants-row p {
    margin: 0;
}

#page-content div.wi-participants-list {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
}

#page-content div.wi-participant-info {
    display: flex;
    align-items: center;
    margin: 6px 32px 6px 0;
}

#page-content div.wi-participant-info span.printuser {
    display: flex;
    align-items: center;
}

#page-content div.wi-participants-title {
    width: 5em;
    flex-shrink: 0;
    font-size: 1.25em;
    font-weight: bold;
    display: flex;
    flex-direction: column;
    height: 60px;
    justify-content: center;
}

#page-content div.wi-avatar {
    width: 48px;
    height: 48px;
    background-color: #F0F0F0;
    box-shadow: inset 0 0 0 1px #00000016;
    border-radius: 50%;
    background-size: cover;
}

#page-content div.wi-participant-info div:not(:last-child) {
    margin-right: 8px;
}

#page-content div.wi-title-block div.wi-avatar {
    width: 24px;
    height: 24px;
    margin-left: 4px;
}

#page-content div.wi-text-block {
    display: flex;
    margin: 1.5em 0;
}

#page-content div.wi-text-block div.wi-avatar {
    margin-right: 24px;
    flex-shrink: 0;
}

#page-content div.wi-speaker p {
    margin: 0;
    display: inline-block;
    padding: 4px 12px;
    background-color: #F0F0F0;
    border-radius: 8px;
    font-weight: bold;
}

#page-content div.wi-bubble {
    padding-left: 1em;
    border-left: 4px solid;
    border-radius: 4px;
}

#page-content div.wi-text blockquote {
    margin: 1em 0;
}

#page-content div.wi-text div.collapsible-block {
    margin: 1em 0;
}

#page-content div.wi-hr {
    width: 100%;
    height: 3em;
    margin: 32px 0;
    background-image: url("https://scpsandboxcn.wikidot.com/local--files/collab:interview/wi-line1.png");
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
}

#page-content div.wi-hr.wi-hr-final {
    background-image: url("https://scpsandboxcn.wikidot.com/local--files/collab:interview/wi-line2.1.png");
}

#page-content div.wi-text img:first-child {
    margin-block-start: 1em;
}

/* Images and Colors */

#page-content div.wi-p-bread div.wi-avatar {
    background-color: #FFA733;
