[[#if {$top} |   | [!-- ]]
[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300&display=swap');

.wikiheading.colmod-block .colmod-link-top a {
  color: black;
  font-family: 'Noto Serif SC', serif;
  font-size: 190%;
}

.wikiheading.colmod-block .colmod-link-top a:hover {
  text-decoration: none!important;
}

.wikiheading.colmod-block .colmod-link-top {
  border-bottom: solid 0.5px lightgray;
  padding-bottom: 5px;
  margin-bottom: 1rem;
}

.wikiheading.colmod-block .unfolded .colmod-link-top a::before {
  display: inline-block;
  padding: 0 6px;
  font-size: 1em;
  content: "\f106";
  font-family: "FontAwesome";
}

.wikiheading.colmod-block .folded .colmod-link-top a::before {
  display: inline-block;
  padding: 0 6px;
  font-size: 1em;
  content: "\f107";
  font-family: "FontAwesome";
}

[[/module]]
