[[module css]]
@import url('https://fonts.googleapis.com/css?family=Gochi+Hand|Source+Sans+Pro&display=swap');

.rmpostcontainer
{
    background-image: url('https://firedawnfolder.wdfiles.com/local--files/component%3Alarueposting/LaRueDesk.png');
    background-repeat: no-repeat;
    width: 100%; max-width: 660px;
    height: 980px;
    margin: 0px;
        padding: 60px 20px 0px 60px;
}

.rmpostcontent
{
    font-family: 'Source Sans Pro', sans-serif;
    font-family: 'Gochi Hand', cursive;
    font-style: normal;
    font-size: 1.30em;
    color: #000;
    width: 530px;
    height: 880px;
    padding: 30px;
    margin: 0px;
    margin-top: -8px;
}

.rmpostmobiletitle
{
    display: none;
}

.scribble
{
    font-style: bold;
    font-size: 1.30em;
    color: #000;
    width: 300px;
    padding: 0px 0px 0px 20px;
    margin: 0px;
    margin-top: -8px;
    -ms-transform: rotate(20deg); /* IE 9 */
    transform: rotate(12deg); /* Standard syntax */
}

@media only screen and (max-width: 600px) {

    .rmpostcontainer {
        background-image: url("http://firedawnfolder.wikidot.com/local--files/component:larueposting/LaRueMobile.png");
        background-size: 100%;
        background-repeat: repeat;
        width: 100%;
        height: auto;
        padding: 0px;
        padding-top: 0px;
    }

    .rmpostcontent {
        font-size: 14pt;
        width: 85%;
        height: auto;
        padding: 10px 10px 0px 30px;
    }

    .rmposttitle {
        width: auto;
        height: auto;
        padding: 0px 20px 0px 70px;
    }

}

