[[module CSS]]
.heritage-notice {
border: dashed 2px #b00010;
border-radius: 10px;
width: 400px;
margin: 0 auto;
padding: 0 20px;
background: #fcfcf0 url(/local--files/heritage-collection/hatch-black10i.png);
color: #331010;
min-height: 116px;
}

table.heritage {
  border: 0;
  padding: 0;
  margin: 0 auto;
  width: 600px;
  border-spacing: 0;
  border-collapse: separate;
}

table.heritage td {
  vertical-align: top;
}

table.heritage td.title,
table.heritage td.label {
  border-right: solid 1px #999;
  font-size: 16pt;
  color: #999;
  width: 200px;
  text-align: right;
  padding-right: 10px;
}

table.heritage td.label {
  font-size: 20pt;
}

table.heritage td.title {
  padding-bottom: 20px;
}
table.heritage td.label {
  font-weight: bold;
}

table.heritage td.entry {
  padding: 6px 0 20px 10px;
}

table.heritage div.description {
  font-size: 14pt;
}
table.heritage div.snippet {
  font-style: italic;
  font-size: 8pt;
  color: #666;
}
table.heritage div.date {
  font-weight: normal;
  font-size: 7pt;
}
table.heritage div.snippet p {
  margin: 5px 0;
}
table.heritage div.description p,
table.heritage div.date p {
  margin: 0;
}

@media (max-width: 599px) {
.heritage-notice {
padding: 0;
}

table.heritage {
width: 100%;
}

table.heritage td.title, table.heritage td.label {
width: 40%;
}

table.heritage td.label {
font-size: 15pt;
}
}
[[/module]]

