[[module CSS]]
#header {
     height: 140px;
     position: relative;
     z-index: 10;
     padding-bottom: 22px;
    /* FOR MENU */
     background: url(http://scpko.wdfiles.com/local--files/cosine-s-artwork/IMG_16092020_191630_%2875_x_71_픽셀%29.jpg) 10px 40px no-repeat;
/* Mobile Media Query */
 @media (max-width: 479px) {
     #header {
         background-position: 0 5.5em;
         background-size: 55px 55px;
    }
     #header h1 a {
         font-size: 100%;
    }
     #header h1, #header h2 {
         margin-left: 66px;
    }
}
/* Small Mobile Media Query */
 @media (max-width: 385px) {
     #header {
         background-position: 5% 5.5em;
    }
     #header h1, #header h2 {
         margin-left: -webkit-calc(66px + 5%);
         margin-left: -moz-calc(66px + 5%);
         margin-left: calc(66px + 5%);
    }
}
/* Note Media Query */
 @media (min-width: 480px) and (max-width: 580px) {
     #header {
         background-position: 0.5em 4.5em;
         background-size: 66px 66px;
    }
     #header h1 a {
         font-size: 120%;
    }
     #header h1, #header h2 {
         margin-left: 80px;
    }
}
/* Mini Tablet Media Query */
 @media (min-width: 581px) and (max-width: 767px) {
     #header {
         background-position: 1em 4em;
         background-size: 77px 77px;
    }
     #header h1 a {
         font-size: 140%;
    }
     #header h1, #header h2 {
         margin-left: 93px;
    }
}
/* Tablet Media Query */
 @media (min-width: 768px) and (max-width: 979px) {
     #header {
         background-position: 1em 4em;
         background-size: 88px 88px;
    }
     #header h1 a {
         font-size: 160%;
    }
     #header h1, #header h2 {
         margin-left: 106px;
    }
}
[[/module]]


