@import url('https://fonts.googleapis.com/css2?family=Permanent+Marker&display=swap');
.titre_partie {
    color : midnightblue;
    border-bottom : 1px solid darkslateblue;
    border-left : 1px solid darkslateblue;
    padding-left : 0.5rem;
}
.titre_partie h1 {
    color: midnightblue;
}
.corps_fiche {
    column-count : auto;
    column-width : 20em;
    column-rule : solid darkslateblue 1px;
    text-align : justify;
}
.titre_revue {
    font-family: "Permanent Marker", cursive;
    text-align : center;
    color : midnightblue;
}

.page_un {
    background-color: lavender;
    box-shadow: 0px 1px 1.9px -4px rgba(0, 0, 10, 0.8), 0px 6px 15px -4px rgba(0, 0, 7, 0.9);
    display: block;
    margin-inline: auto;
    margin-block: 1.5em;
    padding: 1.5rem;
    position: relative;
}

div:is(.page_un)::before, div:is(.page_un)::after {
    content: "";
    height: 99%;
    position: absolute;
    width: 98%;
    z-index: -1;
    background-color: lavender;
}

.page_un::before {
    left: -15px;
    transform: rotate(-2.5deg);
    box-shadow: 0px 1px 1.9px -4px rgb(0, 0, 0), 0px 6px 15px -4px rgb(0, 0, 0);
}

.page_un::after {
    right: -3px;
    top: -10px;
    transform: rotate(1.4deg);
    box-shadow: 0px 1px 1.9px -4px rgb(0, 0, 0), 0px 6px 15px -4px rgb(0, 0, 0);
}
[[/module]]

