$(function() {
    var ua = navigator.userAgent.toLowerCase();
    var bReload= true;
    if ( ( ua.indexOf("applewebkit") >= 0 || ua.indexOf("firefox") >= 0 ) && ua.indexOf("edge") == -1 )
        bReload= false;

    $('.fader').on("click",closeCredit);
    function closeCredit() {
        if ( bReload ) {
            history.go(-1);
            parent.location.href = document.referrer.split('#')[0];
        } else {
            history.go(-1);
        }

        $('.fader').off("click");
        setTimeout(function(){
            $('.fader').on("click",closeCredit);
        },1200);
    }
});
</script>
<style>
.fader {
    width: 100%;
    height: 100%;
    position: fixed;
}
</style>
</head>
<body>
<div class='fader'></div>
</body>
</html>
[[/code]]

