[[module CSS]]
#header {
    background: url("http://scp-wiki.wdfiles.com/local--files/the-ursus-maritimus-incident/logo.png") 15px 40px no-repeat;
    background-size: 6rem;
}

@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em ;
        background-size: 66px 66px ;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em ;
        background-size: 55px 55px ;
    }
}

/* Wilson's Divs */
div.wilson-border {
    border: 0.063em solid hsl(176, 30%, 15%);
    box-shadow: 0em 0.063em 0.125em rgba(0, 0, 0, 0.1);
}

div.wilson-header {
    color: white;
    background: hsl(213, 43%, 57%);
    padding: 1em;
    font-size: 150%;
    font-weight: bold;
    text-align: center;
}

div.wilson-subtitle {
    color: hsl(176, 30%, 15%);
    background: hsl(213, 58%, 73%);
    padding: 0.313em;
    text-align: right;
    font-weight: bold;
}

div.wilson-body {
    color: hsl(176, 30%, 15%);
    background: hsl(213, 70%, 77%);
    padding: 1em;
}

blockquote {
    background: hsl(213, 72%, 82%);
    border-color: transparent;
}
[[/module]]

