[[module css]]
@media screen and (max-width: 800px){
    #header {
        background: url("https://scp-wiki.wdfiles.com/local--files/component:pride-highlighter/tg_logo_blk.svg") 0px 60px no-repeat;
        background-size: 5rem;
    }
    #header h1 a::before{
        font-size: 60%;
    }
    #header h2 span::before{
        font-size: 80%;
    }  
}
#main-content {
margin:2em;
max-width:100%;
}
#page-title{
display:none;
}
div.magPage{
    display: flex;
    align-items:flex-start;
    justify-content:space-around;
}
div.magContent{
    margin-left: 5%;
    margin-right: 5%;
}

.magazine::first-letter {
  font-size: 250%;
  margin-right: 6px;
  float: inline-start;
}
.image-container{
padding:0;
}
.booten{
padding:6px 12px;
text-align:center;
white-space:nowrap;
verticle-align:middle;
background-image:linear-gradient(to bottom,#fff 0,#e0e0e0 100%);
text-shadow:0 1px 0 #fff;
border:solid 1px black;
border-radius:3px;
margin:4px;
color:black;
}
.listing{
color:white;
border-left:3px solid #ccc;
border-right:3px solid #ccc;
border-bottom:3px solid #ccc;
background:#26867C;
padding:1em;
border-radius:0px 0px 10px 10px;
display:flex;
}
.personnel{
flex: 1 1 50%;
margin:4px;
}
.personnel a{
color:red;
pointer-events: none;
}
.division{
color:black;
border:3px solid #ccc;
border-radius:50px 20px;
padding:12px;
background-image:linear-gradient(to bottom, #fff 0%, #e0e0e0 100%);
margin:6px;
}
.addon{
animation-name:hoover;
animation-duration: 2s;
animation-iteration-count: infinite;
}
@media only screen and (max-width: 600px) {
.listing{
display:block;
}
.personnel{
flex:none;
width:100%;
}
img{
object-fit:cover;
width:600px;
height:300px;
}
}
div.terminal{
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #131;
    /* black shadow at the top of the terminal */
    background-image: 
        radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #121);
    background-position: center;
    display: block;
    /* shadow around the terminal */
    box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
    /* prevents scanline from forming a scrollwheel */
    overflow:hidden;
}
div.terminal blockquote {
    background-color: black;
    border: double 3px #80FF80
}
div.scanline{
    margin-top: -40%;
    width: 100%;
    height: 60px;
    position: relative;
    pointer-events: none;
    /* Safari 4.0 - 8.0 */
    -webkit-animation: scan 12s linear 0s infinite; /* You may need to change this. If the bar goes by too quickly, increase 5s. */
    animation: scan 12s linear 0s infinite; /* Same as above */
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.text{
    color: rgba(128,255,128,0.8);
    padding-left: 0em;
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
}

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* You may need to change this depending on the length of your terminal. If the scanline stops halfway, increase the second number.*/
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);} /* Same as above.*/
}

/* -------------- BLINK-BAR -------------- */
span.blinkie {
    animation: blink 1.0s ease-in-out infinite alternate;
}

@keyframes blink {
    to {
        opacity: .0;
    }
}

.danke {
    padding: 5px;
    margin-bottom:10px;
    font-family: monospace;
    font-size: 1.1em; 
}

.agent {
    background-color:#002200;
    border: 3px solid #55AA55;
    color: #77CC77;
}

.site {
    background-color:#222200;
    border: 3px solid #AAAA55;
    color: #DDDD77;

div.text a {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #80FF80;
    padding: 1px;
}
div.text a:hover::before{
    content: "> ";
}

