[[module CSS]]

.fancyhr hr {
    border-top: 2vw solid transparent;
    background-color: rgba(var(--bright-accent), 0);
    height: 0;
    box-sizing: border-box;
    border-image-source: url('http://scpdsandbox.wikidot.com/local--files/nights-into-dreams/darkjacket.png');
    border-image-repeat: round round;
    background: none;
    border-image-slice: 80 500 80 500 fill;
    border-image-width: 10em 80em 10em 80em;
}

@import url('https://fonts.googleapis.com/css2?family=Cinzel+Decorative:wght@800&display=swap')

@import url('https://fonts.googleapis.com/css?family=Charis+SIL:wght@800&display=swap');

h1, #page-title {
    color: red;
    font-family: 'Cinzel Decorative';
    font-size: 200%;
} 

h2, h3, h4, h5, h6 {
    color: red;
    font-weight: bold;
    font-family: 'Cinzel Decorative';
}

 #header h1 a::before{
     font-family: 'Cinzel Decorative';
     margin-left:
}

 #header h2::before{
     font-family: 'Cinzel Decorative';
     left:-100px;
}

.classic {
    display: block;
    font-family: 'Pangolin', cursive;
    font-size: 1.3em;
    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to bottom ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;
    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
@import url(https://fonts.googleapis.com/css2?family=Montserrat:wght@800&display=swap);
:root {
    --accentColor: red;
    --header-title: "NIGHTFALL";
    --header-subtitle: "RELIQUIAS IDENTATIS";
    --lgurl: url(http://smlt.wdfiles.com/local--files/plaguebearer%3Anobody/%3F);
}

#top-bar{display: flex; justify-content: center; right: 0;}
#top-bar ul{padding-right: .67em; padding-left: .67em; background: #ff0000; border-radius: 3.33rem;}
#top-bar ul a{color: #14181f}
.top-bar::before{position: absolute; top: -.4rem; left: calc(100% / 4); width: calc(100% / 2); height: 3px; content: ""; background: linear-gradient(to right, transparent, var(--accentColor) 50%, transparent 100%);}
#extra-div-1{position: absolute; top: 0; width: 100%; height: 11.67em; background: var(--lgurl) center no-repeat; background-size: contain; filter: opacity(.11); z-index: 1;}


body {color: #b6b8ba; background-color: #2F333C; background-image: linear-gradient(to bottom,  #20242c, #20242c 90px,  #20242c 90px, #14181f 200px, #14181f 200px, #14181f 100%);}

.justified {text-align: justify; text-indent: 2.4em;}
hr {background: linear-gradient(to right, transparent, var(--accentColor) 30%, var(--accentColor) 70%, transparent 100%);}
.normal hr {background: var(--accentColor);}
.bt {font-weight: bold; color: var(--accentColor); font-family: 'Josefin Sans', sans-serif;}
h1, h2 {color: var(--accentColor); font-family: "Charis SIL", serif;}
