    --accent: rgba(0, 176, 79, 1);
    --daccent: rgba(3, 143, 66, 1);
    --hraccent: rgba(0, 176, 79, 0.75);
}

#page-content .alertbox {
    width: 600px;
    max-width: 100%;
    margin: 1em auto;
    border-top: solid 3px var(--daccent);
    border-bottom: solid 3px var(--daccent);
    background: rgba(0, 176, 79, 0.05);
    text-align: center;
}

#page-content hr {
    margin: unset;
    padding: unset;
    height: unset;
    border: unset;
    color: unset;
    background-color: unset;
}

#page-content hr {
    border: 0;
    height: 1px;
    background: linear-gradient(to left, rgba(255, 255, 255, 1) 0%, var(--hraccent) 50%, rgba(255, 255, 255, 1) 100%);
    width: 85%;
    margin: 3em auto;
    position: relative;
    overflow: visible;
}

#page-content hr::after {
    display: block;
    content: "\f00d";
    font-family: "FontAwesome";
    font-size: 10px;
    color: var(--accent);
    position: absolute;
    top: -5px;
    left: 50%;
    background: white;
    width: 20px;
    height: 10px;
    transform: translateX(-50%);
}

#page-content .shr hr {
    width: 30%;
    margin: 2em auto;
}

#page-content .sshr hr {
    width: 15%;
    margin: 1em auto;
}

#page-content {
    text-align: center;
}

#page-content h1,
#page-content h2,
#page-content h3 {
    color: var(--accent);
}

#page-content a,
#page-content a.newpage {
    color: var(--daccent);
}

#page-content h1,
#page-content h2,
#page-content h3 {
    margin: 0 0 .75em 0;
}

#page-content .block {
    margin: 1em auto;
    width: 100%;
    max-width: 500px;
    border: solid 0.5px var(--accent);
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link {
    color: var(--accent);
    font-size: 120%;
    font-weight: bold;
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link::after {
    content: "\A（クリックで開きます）";
    white-space: pre;
    color: var(--accent);
    font-size: 10px;
    text-decoration: none!important;
}

#page-content .customcol .collapsible-block-folded a.collapsible-block-link {
    color: var(--accent);
    font-size: 120%;
    font-weight: bold;
}

#page-content .customcol .collapsible-block-unfolded {
    width: 100%;
    max-width: 600px;
    border: solid 0.5px var(--accent);
    margin-left: auto;
    margin-right: auto;
    position: relative;
}

#page-content .customcol .collapsible-block-unfolded-link {
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    background: var(--accent);
    line-height: 2;
    font-size: 120%;
    font-weight: bold;
}

#page-content .customcol .collapsible-block-unfolded-link a {
    color: white;
    text-decoration: none!important;
    width: 100%;
    display: block;
}

#page-content .customcol .collapsible-block-content {
    margin: 3em 1em 1em 1em;
