.warning-box {
    max-width: 100%;
    margin: 2em 0;
    border: solid 2px #ea0032;
    background-color: white;
    text-align: center;
}

.warning-box.ratinglist .inner {
    text-align: left;
}

.warning-box .heading {
    width: 100%;
    padding: 5px;
    margin: 0;
    background: #ea0032;
    color: white;
