}

.warning-box.green a {
    color: var(--daccent);
}

.warning-box.blue {
    border: solid 2px midnightblue;
}

.warning-box.blue .heading {
    background: midnightblue;
    color: white;
}

.warning-box blockquote {
    max-width: 300px;
    padding: 0 10%;
    margin: 1em auto;
    text-align: left;
}


.warning-box .inner {
    padding: 5px 10px;
