}
textarea,
pre {
  border-radius: 0.25rem;
  border: 0.0625rem solid rgb(194, 194, 194);
  overflow-y: auto;
  font-family: 'Andale Mono', 'Courier New', Courier, monospace;
}
textarea {
  display: block;
  margin: 0 auto;
  width: 95%;
}
#toolBody {
  margin: 0 auto;
  box-shadow: 0 0.125rem 0.125rem rgba(0, 0, 0, 0.29);
}
#cssAreaWrap, #interwikiWrap {
  -webkit-transition: height .75s cubic-bezier(0.4, 0, 0.2, 1);
  -moz-transition: height .75s cubic-bezier(0.4, 0, 0.2, 1);
  -o-transition: height .75s cubic-bezier(0.4, 0, 0.2, 1);
  transition: height .75s cubic-bezier(0.4, 0, 0.2, 1);
  overflow: hidden;
}
#interwikiWrap {
  background-color: rgba(0, 135, 189, .15);
}
#toolWidth {
  width: 5em;
  text-align: center;
}
.previewBtn {
  display: block;
  margin: 0 auto;
  width: 80%;
  font-size: 1.75em;
}
.code {
  border: 0.0625rem dashed #DDD;
  background-color: #F7F7F7;
  font-family: 'Andale Mono', 'Courier New', Courier, monospace;
  padding: 0 1em;
  margin: 0.4em 0;
  white-space: pre-wrap;
}
#popupWrap {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 1;
  -webkit-transition: opacity .2s ease-in-out;
  -moz-transition: opacity .2s ease-in-out;
  -o-transition: opacity .2s ease-in-out;
  transition: opacity .2s ease-in-out;
}
#popupWrap.folded {
  pointer-events: none;
  opacity: 0;
}
.fader {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(34, 34, 34, .5);
  cursor: default;
}
#popup {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  width: 90vw;
  max-width: 31.25rem;
  height: 21.875rem;
  margin: auto;
  padding: 0.55em 1em;
  background-color: #fefefe;
  box-shadow: 0 0.0625rem 0.1875rem rgba(0, 0, 0, 0.5);
}
#popupContent {
  height: 100%;
  overflow-y: auto;
}
#interwikiCode {
  margin: 0;
  height: 10rem;
  white-space: pre-wrap;
  word-break: break-all;
}
