      document.getElementsByTagName("meta")[2].content = "width=" + metaWidth + ", initial-scale=1.0";
    break;
    case 2: case 3:
      var cssArea = document.getElementsByName("cssArea");
      cssArea[mode - 2].innerHTML = value;
    break;
    case 4:
      window.interwikiFrame.changeStyle(value);
      document.getElementsByName("styleFrame")[0].src = "https://scp-wiki-cn.wdfiles.com/local--files/nav%3Aside/styleFrame.html?" + encodeURIComponent(value);
    break;
  }
  if (mode <= 1) {
    window.parent.recordData(mode, value);
  }
}
