/* RATEBOX */
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background: #333333;
    border: solid #333333 1px!important;
    border-radius: 0em;
    font-family: 'Noto Serif JP', serif;
    box-shadow: none;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #333333 1px!important;
    border-radius: 0em;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
{
    color: rgb(255, 255, 255);
    font-family: 'Noto Serif JP', serif;
}
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: rgb(255, 255, 255);
    border-radius: 0em;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #333333!important;
    box-shadow: none;
    border-radius: 0em;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #fff!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: White!important;
    color: #333333!important;
}

#page-content .rate-box-with-credit-button .creditButton {
    background: #333333;
    border: solid #333333 1px!important;
    border-radius: 0em;
    color: white;
    height: 18px;
    width: 25px;
    position: relative;
    left: -5px;
    box-shadow: none;
}
#page-content .rate-box-with-credit-button .creditButton:hover {
    background: #ffffff;
    border: 1px solid #111111;
    color: #333333!important;
    box-shadow: none;
    filter: invert(0%)
}
#page-content .rate-box-with-credit-button .creditButton a:hover {
    color: #333333;
}
#page-content .modalbox .credit {
    color: black;
}
#page-content .modalbox h2 {
    color: #ffffff;
}
.modalbox .modalbox-title {
    background: #333333!important;
    border-radius: 0 !important;
}
.modalbox {
    box-shadow: 0 2px 6px #cccccc !important;
    border-radius: 0 !important;
    background-color: White  !important;
}


/* トップバー */
div#container-wrap {
  background: url(https://scp-jp.github.io/files/util/storage/media/component/theme/header-bg.png) top left repeat-x, var(--c-header) top left repeat-x;
  background-size: 100px 141px;
}
#container-wrap {
  --c-header: linear-gradient(180deg, black 25%, #002bbd 100%);
  --c-topbar: linear-gradient(180deg, #535353 0%, #3c3c3c 53%);
}
div#container-wrap::after {
  content: "";
  display: block;
  position: absolute;
  top: 140px;
  left: 0;
  width: 100%;
  height: 23px;
  box-shadow: 3px 2px 25px -2px rgba(0,0,0,0.75);
  background: var(--c-topbar) top left repeat-x;
}
/* トップバー */

/* 背景 */

#page-title {
    color: #F22;
}

.colmod-content.change-bg-content {
  position: relative;
  pointer-events: none;
  z-index: -9;
  opacity: 1;
  position: relative;
  pointer-events: none;
  z-index: -9;
  transition-duration: 0.2s;
  -webkit-transition-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
  -moz-transition-timing-function: cubic-bezier(0.3649610494, 0, 0.6350389506, 1);
  transition-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
}
li.folded > .colmod-content.change-bg-content {
  opacity: 0;
  display: block;
}
.bg {
  background: #fff;
  height: 100%;
  width: 100%;
  position: fixed;
  top: 0;
  left: 0;
}

a,a:visited {
    color: #F22;
}
.bg > p {
  display: none;
}
#html-body{
background-color:#333;
}
/* 背景 */

/* ボタン */
[class|="colmod-link"] div.foldable-list-container.change-bg-container {
  display: block;
  position: relative;
  box-sizing: border-box;
  border-radius: 15px;
  width: 50px;
  height: 30px;
  margin: 10px auto;
  background: #b22;
  transition-duration: 0.2s;
  -webkit-transition-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
  -moz-transition-timing-function: cubic-bezier(0.3649610494, 0, 0.6350389506, 1);
  transition-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
}
li.folded > [class|="colmod-link"] div.foldable-list-container.change-bg-container {
  background: #888;
}
[class|="colmod-link"] div.foldable-list-container.change-bg-container::after {
  content: "";
  display: block;
  position: absolute;
  box-sizing: border-box;
  border-radius: 13px;
  width: 26px;
  height: 26px;
  background: #fff;
  top: 2px;
  left: 22px;
  transition-duration: 0.2s;
  -webkit-transition-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
  -moz-transition-timing-function: cubic-bezier(0.3649610494, 0, 0.6350389506, 1);
  transition-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
}
li.folded > [class|="colmod-link"] div.foldable-list-container.change-bg-container::after {
  left: 2px;
}
