#content-wrap {
    background-image: url(http://scpdsandbox.wikidot.com/local--files/gaia/earthrise.jpg);
    background-attachment: fixed;
    background-position: center center;
    background-size: 100%;
}

#page-content{
    overflow: hidden;
    background: rgba(32, 32, 32, 0.75);
    border-radius: 10px;
}

.yui-content {
    border-color: transparent!important;
}

:root {
    --circle-width: 500px;
}

div.circle {
    margin: 0 auto;
    width: var(--circle-width);
    height: var(--circle-width);
    text-align: center !important;
    background: url("http://scpsandboxcn.wikidot.com/local--files/darry14/logo.svg") center no-repeat;
    background-size:calc(var(--circle-width) * 17/40) calc(var(--circle-width) * 17/40);
    position: relative;
    display:flex;
    justify-content: center;
    z-index: 1;
    content: "";
    opacity: 1;
}

div.circle-1 {
    margin: 0 auto;
    width: var(--circle-width);
    height: var(--circle-width);
    text-align: center !important;
    background: transparent;
    position: relative;
    border-radius: 50%;
    display:flex justify-content: center;
    font-family: 'Monoton', cursive;
    font-size: 350%;
    color: rgba(255, 255, 255, 0.9) !important;
    animation: spin 6s ease-in-out infinite;
}

div.circle-1 div {
    width: var(--circle-width);
    height: var(--circle-width);
    position: absolute;
    transform-origin: 50% calc(var(--circle-width)/2);
    text-align: center;
}

div.circle div.letter-1 {
    transform: rotate(calc(360deg / 46 * 2));
}

