.article-box {
    width: 100%;
    margin: 7px 10px;
    padding: 2px 5px 2px 15px;
    border-left: 7px solid orange;
    background-color: rgba(35, 35, 35, 0.9);
}

.article-box-number {
    position: absolute;
    display: flex;
    align-items: center;
    right: 20px;
    color: orange;
    font-size: 45px;
    font-weight: 900;
}

article-box-container {
    position: relative;
    top: 0;
}

@keyframes spin {
    0% {transform: rotateZ(0deg);}
    85% {transform: rotateZ(360deg);}
    100% {transform: rotateZ(360deg);}
}

@media only screen and (max-width: 640px) {
    #content-wrap {
        background-size: cover;
    }
}



/* Grid layout */

.left-grid {
    float: left;
    width: 49%;
    height: 110px;
}
.right-grid {
    float: right;
    width: 49%;
    height: 110px;
}

/* Mobile Media Query */
@media (max-width: 479px) {
.left-grid,
.right-grid {
    width: 100%;
    float: left;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
.left-grid,
.right-grid {
    width: 100%;
    float: left;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
.left-grid,
.right-grid {
    width: 100%;
    float: left;
}
}
