[[include :scp-wiki:theme:minimal
|title=SCPF ARCHIVES
|subtitle=SALVAGE, CATALOGUE, PRESERVE]]
[[module css]]

:root {

/* i cannot remove this part or the css will break, i know it seems redundant, sorry */

     --header-title: 'SCP Foundation Archives';

      --header-subtitle: 'SALVAGE, CATALOGUE, PRESERVE';
        }

@media only screen and (max-width: 768px) {
    #header h1 a,
    #header h2 span {
        margin-left: 0.1rem;
    }
}



#header {
    background-image: url(http://scp-wiki.wikidot.com/local--files/dave/scparchives_logo.png)
}

div#container-wrap {
    background: url(http://scp-wiki.wikidot.com/local--files/dave/bg.png);
background-repeat: repeat-x;
}

#side-bar .side-block {
    background-color: silver !important;
    border-color: transparent;
    border-radius: 0px;
    box-shadow: none;
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background:  #e8e8e8;
    color: black;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: black;
    color: #e8e8e8;
}
#side-bar img,
iframe.scpnet-interwiki-frame {
    filter: grayscale(20%) saturate(80%);
    -webkit-filter: grayscale(20%) saturate(80%);
}
blockquote,
div.blockquote,
#toc,
.code {
     background-color: #e8e8e8;
}

#login-status {
    color: black;
    filter: grayscale(0%) saturate(100%);
    -webkit-filter: grayscale(0%) saturate(100%);
}
div.yui-navset div.yui-content {
    color: #000000;
    background: #e8e8e8;
    border-left: silver outset 2px;
    border-right: silver outset 2px;
    border-bottom: silver outset 2px;
    border-top: silver outset 2px;
}
[[/module]]

