[[module CSS]]
.blurblock {
  margin: 1em 0;
}
.blurblock > * {
  background: lightgray;
  filter: blur(1em);
  color: transparent;
  padding: 2em 0;
  margin: 2em 0;
  transition: all .5s linear;
  will-change: filter;
}
.blurblock > *:hover,
.blurblock > *:focus {
  background: transparent;
  filter: blur(0);
  color: #333;
  transition: all .3s linear;
}
.blurblock > *:first-of-type {
  padding-top: 0;
}
.blurblock > *:last-of-type {
  padding-bottom: 0;
}

.trueblur {
  filter: blur(.05em);
}
.finalblur {
  filter: blur(.1em);
  transition: opacity .5s linear;
}
.finalblur:hover,
.finalblur:focus {
  opacity: 0;
  transition: opacity .5s linear;
}

@media (hover: hover) {
  .no-pointer {
    display: none;
  }
}
@media (hover: none) {
  .hover-pointer {
    display: none;
  }
}
[[/module]]

