[[module CSS]]
/*
    Powered on 2019/07/01 23:01:37
    [2019 Wikidot Theme]
    Created by Sreason29
    CC BY-SA 3.0
    Modified by ColorlessL, partly based on Third Law Canon Theme(CC-BY SA 3.0)
*/

h1, #page-title {
    color: #B0E0E6;
}

body {
    background: rgb(39, 49, 112);
    color: #FFFFFF;
}
a,a:visited {
    color: rgba(246, 178, 107,0.8);
}
h2,h3,h4,h5,h6 {
    color: #BEC3A9;
}

div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #31353D!important;
    border: solid #31353D 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #31353D!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #B0E0E6;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #B0E0E6!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #31353D!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

blockquote {
    border: 1px
    dashed #C7AF3C;

    background: #567093;
}
div#container-wrap {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/collab%3Asreason29-2/fin3.png) top left repeat-x;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "海   葬";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "深海遗葬，一梦黄粱";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

#side-bar {
    clear: none;
    float: none;
    position: absolute;
    top: 0.5em;
    left: 2em;
    width: 17em;
    padding: 0;
    border: none;
    display: block;
    background: transparent;
}

#side-bar a,#side-bar a:visited{
    color: #3A3F60;
}

#side-bar img {
     filter: hue-rotate(225deg) brightness(80%) invert(10%);
    -webkit-filter: hue-rotate(225deg) brightness(80%) invert(10%);
}

#side-bar div.side-block:nth-child(5) > div:nth-child(2) > div:nth-child(1) img {
    filter: none;
    -webkit-filter: none;
}

div.scpnet-interwiki-wrapper {
    filter: hue-rotate(45deg) brightness(80%) invert(90%);
}

#side-bar .side-block {
    padding: 10px;
    border: 1px solid #C7AF3C;
    border-radius: 10px;
    background: #567093 !important;
    box-shadow: none !important;
    margin-bottom: 15px;
}
#side-bar .side-area {
    padding: 10px;
}
#side-bar .heading {
    color: #dddddd;
    border-bottom: solid 1px #dddddd;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
#side-bar p {
    margin: 0;
}
#side-bar div.menu-item {
    margin: 2px 0;
}
#side-bar div.menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
}

#side-bar div.menu-item a {
    font-weight: bold;
}
#side-bar div.menu-item.inactive img {
    opacity: 0.25;
}
#side-bar div.menu-item.inactive a {
    color: #BEC3A9;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #BEC3A9;
}
#side-bar div.menu-item.sub-item {
}

#side-bar .collapsible-block-folded {
    background: none;
}

#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px;
    color: #BEC3A9;
    border-color: #BEC3A9;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color: #BEC3A9;
    border-color: #BEC3A9;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
        color: #BEC3A9;
    border-color: #BEC3A9;
    text-decoration: none;
}

#side-bar ul{
    list-style-type: none;
    padding: 0 5px 0;
}

div.yui-navset div.yui-content {
    background: #3A3F60;
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #A99786!important;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #BEAA79!important;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #31353D!important;
}

div.open-menu a {
    color: rgb(60, 120, 216)!important;
    border-color: rgb(60, 120, 216)!important;
}
/* SEARCH TOP BOX FROM    
Third Law Canon Theme
    [2016 Wikidot Theme]
    Created by GreenWolf, with technical assistance from Randomini and aesthetic assistance from Dr Kens and tawny.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan.
    The Station font was created by Nick Matavka and is licensed under the SIL Open Font License.
    The Prometheus Labs/Third Law Flame Logo was created by GreenWolf and is licensed under CC-BY-SA 3.0.
    Edited for SCP基金会 by Izsac*/
#search-top-box {
    position: absolute;
    top: 79px;
    right: 9px;
    width: 250px;
    text-align: right;
}
#search-top-box-input {
    border: solid 1px #0c1121;
    border-radius: 5px;
    color: #e9ebf0;
    background-color: #222e59;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #bfc3d4;
    color: #e9ebf0;
    background-color: #404d7e;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
    border: solid 1px #0c1121;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #e9ebf0;
    background-color: #222e59;
    background: linear-gradient(to bottom, #263464,#1e284e);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #bfc3d4;
    color: #e9ebf0;
    text-shadow: 0 0 1px rgba(255,255,255,.25)
    background-color: #222e59;
    background: linear-gradient(to bottom, #1e284e,#263464);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

/* TOP BAR RECOLORING*/
#top-bar ul li ul {
    border: solid 1px rgb(39, 49, 112);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #D0F6F6;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #EFFFFF;
    color: rgb(39, 49, 112);
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #EFFFFF;
}

/* basic Query for mobile devices */

@media (max-width: 767px) {
.owindow {
    min-width: 80%;
    max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
    float: left;
}

.owindow .button-bar {
    float: right;
}

.owindow div a.btn-primary {
    width: 100%;
    float: left;
}

.mobile-top-bar ul li:last-of-type ul {
    right: 0;
}

span, a {
word-break: break-all;
}
}

/* Mobile Media Query */
@media (max-width: 479px) {
#search-top-box-input {
    width: 5em;
}

#page-content {
    font-size: 0.9em;
}

#main-content {
    margin: 0;
}

#recent-posts-category {
width: 100%;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

#side-bar {
    width: 80%;
    position: relative;
}

.top-bar {
    display:none;
}

.mobile-top-bar {
    display: block;
    padding: 0;
}

.page-options-bottom a {
    padding: 0 4px;
}

#header h1 a {
    font-size: 100%;
}

blockquote {
    margin: 1em 0;
}

.license-area {
    font-size: 0.8em;
}

#header {
    background-position: 0 5.5em;
    background-size: 55px 55px;
}

#header h1, #header h2 {
    margin-left: 66px;
}

table.form td, table.form th {
    float: left;
}

/*
td.title {
    width: 30%;
}
*/

td.name {
    width: 15em;
}

table.form td, table.form th {
    padding: 0;
}

#edit-page-title {
    max-width: 90%;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}

#page-content div.title {
word-break: keep-all;
}

}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
#search-top-box-input {
    width: 7em;
}

#main-content {
    margin: 0 2em 0 2em;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

#side-bar {
    width: 80%;
    position: relative;
}

.top-bar {
    display:none;
}

.mobile-top-bar {
    display: block;
}

.page-options-bottom a {
    padding: 0 5px;
}

#header h1 a {
    font-size: 120%;
}

blockquote {
    margin: 0.5em;
}

.license-area {
    font-size: 0.85em;
}

#header {
    background-position: 0.5em 4.5em;
    background-size: 66px 66px;
}

#header h1, #header h2 {
    margin-left: 80px;
}

/*
td.title {
    width: 30%;
}
*/

#page-content div.title {
word-break: keep-all;
}

td.name {
    width: 15em;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}
/* images*/
 .scp-image-block {
    background-color: #747474 !important;
}

.scp-image-block .scp-image-caption {
    background-color: #747474 !important;
}

.scp-image-block .scp-image-caption > p {
    padding: 0 5px;
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) {
#search-top-box-input {
    width: 8em;
}

#side-bar {
    width: 80%;
    position: relative;
}

#main-content {
    margin: 0 3em 0 2em;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

.top-bar {
    display: none;
}

.mobile-top-bar {
    display: block;
}

.page-options-bottom a {
    padding: 0 6px;
}

#header h1 a {
    font-size: 140%;
}

.license-area {
    font-size: 0.9em;
}

#header {
    background-position: 1em 4em;
    background-size: 77px 77px;
}

#header h1, #header h2 {
    margin-left: 93px;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#main-content {
    margin: 0 4em 0 20em;
}

#header, #top-bar #side-bar {
    max-width: 100%;
}

.top-bar li {
    margin: 0;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    width: 110px;
}

.page-options-bottom a {
    padding: 0 7px;
}

#header h1 a {
    font-size: 160%;
}

.license-area {
    font-size: 0.95em;
}

#header {
    background-position: 1em 4em;
    background-size: 88px 88px;
}

#header h1, #header h2 {
    margin-left: 106px;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}

/* Desktop Media Query -----------
@media (min-width: 980px) and (max-width: 1399px) {

}
------------------------------------------ */

/* Wide Monitor Media Query -----
@media (min-width: 1400px) {

}
------------------------------------------ */

/* off-canvas */

.close-menu {
    display: none;
}

@media (max-width: 767px) {

.page-history tbody tr td:last-child {
width: 35%;
}

.owindow {
    min-width: 80%;
    max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
    float: left;
}

.owindow .button-bar {
    float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
    display: none;
}

.open-menu a {
    position: fixed;
    bottom: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: 0.2em solid #888 !important;
    background-color: #fff !important;
    border-radius: 3em;
    color: #888 !important;
}

.open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
}

#main-content {
    max-width: 90%;
    margin: 0 5%;
    padding: 0;
    -webkit-transition: 0.5s ease-in-out 0.1s;
    -moz-transition: 0.5s ease-in-out 0.1s;
    -ms-transition: 0.5s ease-in-out 0.1s;
    -o-transition: 0.5s ease-in-out 0.1s;
    transition: 0.5s ease-in-out 0.1s;
}

#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 17em;
    height: 100%;
    background-color: rgb(54, 100, 139);
    overflow-y: auto;
    z-index: 10;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);

}

#side-bar:target {
    display: block;
    left: 0;
    width: 17em;
    margin: 0;
    border: 1px solid #dedede;
    z-index: 10;
}

#side-bar:target + #main-content {
    left: 0;
}

#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
}
}

