[[module CSS]]
:root {
  --fade-in-delay: 0.05s !important;
}
#header {
    height: 290px;
}

#header h1 a {
    padding-top: 210px;
}

#header h1 a::before {
    content: inherit;
}

#header h2 {
    display: none;
}

#top-bar {
    top: 16rem;
}

div#extra-div-1 {
    height: 210px;
    margin-top: 2em;
    background-image: url('http://scpko.wikidot.com/local--files/deicidium/Project_Tartarus');
}
[[/module]]
[[module css]]
.page {
    display: block;
    overflow: hidden;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-style: normal;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.page p {
    line-height: 20px;
    margin: 0;
}
[[/module]]

