--title:"R.S. MNEMOSYNE";
--subtitle:"DEPARTMENT OF DELETIONS";
--sidesubtitle: "DELETIONS";
--sidelogo: url('https://scp-wiki.wdfiles.com/local--files/scp-6768/dept.png');
--header-font-primary: 'Poppins';
--body-font-primary: 'Zen Kaku Gothic Antique', sans-serif;
--ACS-font: var(--body-font-primary);
--five-color: 224, 107, 136;
--basalt-undertone: 18, 18, 18;
