--vector-orange: var(--basalt-alternate-color);
--hr-color: 0, 0, 0;
}
#page-content :is(h3,h4,h5,h6) > span, blockquote, div.blockquote { border-width: 0;  font-size: .9em !important;}



::-webkit-scrollbar-thumb {
background-color:rgb(var(--basalt-alternate-color));

}
#page-title, .meta-title {
border: none !important;
text-align: center;
}

#page-content .widetable .wiki-content-table {
    width: 100%;
    font-size: 1.5em;
}
#header h1 a span {
    filter: brightness(180%);
    filter: contrast(180%);
}

.a {
padding-right:3px;
text-align:right;
} 
.b {
opacity:90%;
display:block;
margin:4px;
}
.c {
background-color: rgb(55, 55, 55);
max-width:30rem !important;
border-radius:6px 6px 2px 2px;
display:block;
margin:0 auto;
margin-bottom: 15px;
}
.d, .e {
padding:5px;

}
.d {
margin:15px;
}
.e {
border: 1px solid black;
margin: 30px;
background-color: #606060;
padding-left:12px;
}
body {
background-color: #000000;
background-attachment: fixed;

}
.yui-navset.yui-navset-top .yui-content {

border: none;
}
a {
font-family: var(--header-font-primary);
}

.row {
  display: flex;
  flex-wrap: wrap;
  padding: 0 4px;
}
.ep {padding:1rem;}
.column {
  flex: 24%;
  max-width: 24%;
  padding: 0 4px;
}

.column img {
  margin-top: 8px;
  vertical-align: middle;
  width: 100%;
}
.twocolumn {
    flex: 100%;
    max-width: 100%;
    margin-top: 8px;
}
.ep h5 {letter-spacing: 1em;}
.ep h5 a {letter-spacing: 3px;}
.rec h5 {letter-spacing: 5px;}
.rec h5 a {letter-spacing: 3px;}
@media screen and (min-width: 801px) {
.epimg {width:200px;height:200px;float:right; margin: auto;}
.colimg:hover {
  transform: scale(2); 
}
.colimgsm:hover {
  transform: scale(1.5); 
}
.colimglg:hover {
  transform: scale(3); 
}
h5 {font-size:1.4em;}

}


@media screen and (max-width: 800px) {
.epimg {width:75px;height:75px;float:right;margin: 36px 8px 0px 0px;}

  .column {
    flex: 45%;
    max-width: 45%;
  }
  .twocolumn {
    flex: 100%;
    max-width: 100%;
}
.colimg:hover {
  transform: scale(1.5); 
}
.colimgsm:hover {
  transform: scale(1.25); 
}
.colimglg:hover {
  transform: scale(1.75); 
}
table.wiki-content-table {
font-size: 75%;
}
h5 {font-size:.9em;}
}
@media screen and (max-width: 480px) {

.epimg {display:none}
  .column {
    flex: 90%;
    max-width: 90%;
  }
h5 {font-size:.83em;}
}

