[[module CSS]]
@import url(http://ja.scp-wiki.net/deathcontest/code/1);
[[/module]]

[[module CSS]]

div.page-rate-widget-box .rate-points {
    background-color: #333333;
    border: solid 1px #333333;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #ffffff;
    border-top: solid 1px #333333;
    border-bottom: solid 1px #333333;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #333333;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #333333;
    color: #cccccc;
}
.page-rate-widget-box .cancel {
    background-color: #333333 !important;
    border: solid 1px #333333 !important;
}
.page-rate-widget-box .cancel a {
    color: #333333;
}
.page-rate-widget-box .cancel a:hover {
    background-color: #333333;
    color: #ffffff;
}
#page-content .rate-box-with-credit-button .creditButton {
    background-color: #333333;
    border: 1px solid #333333;
}

#page-content .rate-box-with-credit-button .creditButton:hover {
    background-color: #acacac;
    border: 1px solid #acacac;
}

#page-content .rate-box-with-credit-button .creditButton a:hover {
    color: white;
}

[[/module]]

[[module CSS]]
@import url(http://ja.scp-wiki.net/deathcontest/code/1);

#page-content p {
    font-family: 'ComicNeueAngularRegular', 'MS Comic Sans', 'Comic Sans', sans-serif;
}
[[/module]]
