[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@200&display=swap');
.shushuInner {
  margin: 3em auto;
  padding: 1em;
  background-color: #fff;
  box-shadow: 0 0 0 1px #000, 0 0 0 3px #fff, 0 0 0 6px #000;
}

.shushuInner ol {
  list-style-type: cjk-ideographic;
}

.shushuInner h4 {
  margin: 0 0 0.6em;
  padding: 0.75em 0;
  border-bottom: 1px solid #bbb;
  color: #486a8a;
  font-family: "ヒラギノ明朝 ProN W6", "HiraMinProN-W6", "HG明朝E", "ＭＳ Ｐ明朝", "MS PMincho", "MS 明朝", serif;
  font-size: 200%;
  font-weight: normal;
  text-align: center;
  -webkit-font-feature-settings: "trad";
  -moz-font-feature-settings: "trad";
  font-feature-settings: "trad";
  font-variant-east-asian: traditional;
  unicode-bidi: bidi-override;
}

.shushuInner h4>span {
  display: block;
  margin: 0 -0.5em;
}

.shushuInner h4>span span {
  display: inline-block;
  position: relative;
}

.shushuInner h4>span span:before, .shushuInner h4>span span:after {
  display: block;
  position: absolute;
}

.shushuInner h4>span span:first-of-type {
  margin-bottom: 0.25em;
  padding: 0 1em;
}

.shushuInner h4>span span:first-of-type:before {
  top: 0;
  left: 0;
  content: "「";
}

.shushuInner h4>span span:first-of-type:after {
  right: 0;
  bottom: 0;
  content: "」";
}

.shushuInner h4>span span[style="font-size:60%;"] {
  padding: 0 0.5em;
  text-indent: 0.25em;
  letter-spacing: 0.25em;
}

.shushuInner h4>span span[style="font-size:60%;"]:before {
  top: 0;
  left: 0;
  content: "(";
}

.shushuInner h4>span span[style="font-size:60%;"]:after {
  right: 0;
  bottom: 0;
  content: ")";
}

.shushuInner blockquote {
  border: 1px solid #c8c8c8;
  background: -webkit-linear-gradient(285deg, #f0f0f0 0%, #fff 2%, #fff 8%, rgba(255, 255, 255, 0.69) 18%, rgba(255, 255, 255, 0.19) 34%, rgba(255, 255, 255, 0) 40%, rgba(255, 255, 255, 0.06) 51%, rgba(255, 255, 255, 0.14) 66%, rgba(255, 255, 255, 0.27) 93%, #f0f0f0 99%), url("http://shitake-crude-production.wdfiles.com/local--files/shushuin-format/clearPaper.png");
