.shushuInner .imageFrame.center {
  float: none;
  clear: both;
  margin: 1em auto;
}

.shushuInner .imageFrame:before {
  position: absolute;
  background-repeat: no-repeat;
  content: "";
}

.shushuInner .imageFrame p {
  margin: 0 0 0.6em;
  text-align: center;
  line-height: 0.7;
