[[module css]]
:root{
    --header-title: "SITE-81ET";
    --header-subtitle: "人を呪わば穴ふたつ";
    --main-accent: 186, 167, 204;
    --sub-accent: 186, 167, 204;
    --bg: 180, 180, 180;
    --body-bg: 250, 249, 252;
    --logo: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A3438721-302-js60/Site-81ET.svg);
}

div#extra-div-1 { background-image: var(--logo); filter: opacity(20%); }
#page-content { width: 100%; }
#page-content tr th { padding: 0px 1em; border: none; line-height: 1.5; }
#page-content tr td { padding: 0px; border: none; line-height: 1.5; }

span:is(.ut){ font-weight: bold; color: rgb(var(--main-accent)); }
span:is(.ot){ font-weight: bold; color: #d59493; }
span:is(.bt){ font-weight: bold; color: #94d5d5; }

.box2 { background-color: rgb(245 245 245) !important; border: none !important; border-left: solid 10px rgb(var(--main-accent)) !important; box-sizing: border-box; border-radius: 10px; margin: 2vh auto; }
.box2 blockquote, .box2 div.blockquote { background: rgb(var(--body-bg)); border: solid 1px rgb(var(--bg),.25); border-radius: 10px; }

.title_container { position: relative; margin-bottom: 60px; }
.title_header { width: fit-content; margin: auto; margin-top: 30px; padding-right: 8px; border: solid 2px rgb(var(--main-accent)); border-radius: 5px; background: rgb(252,252,252); font-weight: bold; }
.title_header p { margin: 0; line-height: 26px; }
.title_header p:not(.title_number) { color: rgb(var(--main-accent)); }
.title_number { color: rgb(252,252,252); background: rgb(var(--main-accent)); margin-right: 5px; padding-inline: 10px; display: inline-block; }
.title_container:after { content: ''; position: absolute; width: 100%; height: 6px; bottom: -15px; background: rgb(var(--main-accent)); }

.box5_logo{ background: var(--logo); background-size: 60%; background-repeat: no-repeat; background-position: center; margin: 20px 10%; }

.box5_cover1{ background: rgba(var(--sub-accent), 0.5); border-radius: 10px; border: solid 2px rgb(var(--sub-accent)); height: 100%; padding: auto; }

.box5_cover2{ background: rgba(255, 255, 255, 0.75); border-radius: 10px; height: 100%; padding: 10px; }

.kuro { color: #000; background: #000; }
.kuro a { color: #000; background: #000; text-decoration: none; }

@media only screen and (min-width: 700px) { .footnote { width: 30em; } }

.rokubou{ display: inline-block; border: dashed 1px #444444; background:url(http://scp-jp.wikidot.com/local--files/mishary-s-proposal/shushuin.png) center no-repeat, rgba(255, 182, 193, 0.25); background-blend-mode: lighten; margin-left: auto; margin-right:auto; font-family: serif; width: 98%; padding: 10px; }

.hidden-link a {
    color: #333333;
}
.hidden-link a:hover {
    text-decoration: none;
}
.hidden-link a:active {
    text-decoration: none;
}

.ult{
    font-weight: bold; font-size: 2em; border-bottom: 4px solid rgb(var(--main-accent));
}
[[/module]]

[[footnoteblock]]
