/* Theme Modifying */
#top-bar ul li a::before,
#top-bar ul li a::after {
    content: unset;
}
#side-bar {
    box-sizing: border-box;
}
.rate-box-with-credit-button,
.page-rate-widget-box span,
.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    height: 16px;
    font-family: verdana,arial,helvetica,sans-serif;
    font-size: 1em;
}
.page-rate-widget-box .rateup a::before,
.page-rate-widget-box .ratedown a::before,
.page-rate-widget-box .cancel a::before {
    content: "";
}
.page-rate-widget-box .cancel {
    padding: 0;
}
.page-rate-widget-box .cancel a {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    margin: 0 1px;
    box-sizing: border-box;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
.rate-box-with-credit-button .creditButton {
    background-color: unset;
    border: none 0;
    border-radius: 0;
    box-shadow: none;
    margin-left: 0;
}
.rate-box-with-credit-button .creditButton a {
    color: #fff;
}
.modalbox .modalbox-title {
    border-radius: 0;
}

/* Content */
.archived > .colmod-block > ul > li > .colmod-link-top div a::before,
.interview .colmod-link-top a:nth-child(2)::before {
    content: "[";
}
.archived > .colmod-block > ul > li > .colmod-link-top div a::after,
.interview .colmod-link-top a:nth-child(2)::after {
    content: "]";
}
.archived > .colmod-block > ul > li > .colmod-content {
    border: solid hsl(0,0%,80%) 1px;
    background: hsl(0,0%,92%);
    padding: 0 0.75em;
}
.archived .blockquote {
    border: dashed hsl(0,0%,20%) 1px;
    background: hsl(0,0%,96%);
}
.archived .collapsible-block + .collapsible-block {
    margin-top: 1em;
}
.archived .collapsible-block:nth-of-type(3) .collapsible-block-content {
    display: none !important;
}
.interview .colmod-block + .colmod-block {
    margin-top: 1em;
}
.interview .colmod-block:nth-of-type(3) .colmod-content {
    display: none !important;
}
