    width: var(--rate-module-button-size);
    height: var(--rate-module-button-size);
}
.rate-box-with-credit-button .creditButton a::before {
    content: "";
    display: block;
    background-color: rgb(var(--basalt-main-text-color));
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-mask-image: var(--vote-info-mask);
    mask-image: var(--vote-info-mask);
    -webkit-mask-size: 27.5%;
    mask-size: 27.5%;
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
    -webkit-mask-position: center;
    mask-position: center;
}
.rate-box-with-credit-button .creditButton a::after {
    content: "";
    display: block;
    position: absolute;
    bottom: 0;
    width: 100%;
    height: 0.05em;
    background-color: rgb(var(--general-border-color));
    z-index: -1;
    transition: all var(--button-hover-transition);
}
.rate-box-with-credit-button .creditButton a:hover::after {
    height: 100%;
}
.modalbox .modalbox-title {
    border-radius: 0;
}
