.heritage-notice {
    border: dashed 1px #b00010;
    border-radius: 1px;
    width: 700px;
    margin: 0 auto;
    padding: 0 20px;
    color: #331010;
    min-height: 116px;
}

table.heritage {
    border: 0;
    padding: 0;
    margin: 0 auto;
    width: 700px;
    border-spacing: 0;
    border-collapse: separate;
}

table.heritage td {
    vertical-align: top;
}

table.heritage td.title,
table.heritage td.label {
    border-right: solid 1px #999;
    font-size: 10pt;
    width: 120px;
    text-align: right;
    padding-right: 10px;
}

table.heritage td.label {
    font-size: 20pt;
}

table.heritage td.title {
    padding-bottom: 10px;
}
table.heritage td.label {
    font-weight: bold;
}

table.heritage td.entry {
    padding: 1px 0 10px 10px;
}

table.heritage div.description {
    font-size: 14pt;
}
table.heritage div.snippet {
    font-size: 10pt;
}
table.heritage div.date {
    font-weight: normal;
    font-size: 10pt;
}
table.heritage div.snippet p {
    margin: 0px 0;
}
table.heritage div.description
p,table.heritage div.date p {
    margin: 0;
}

@media (max-width: 599px) {
    .heritage-notice {
        padding: 0;
    }

    table.heritage {
        width: 100%;
    }

    table.heritage td.title, table.heritage td.label {
        width: 30%;
    }

    table.heritage td.label {
        font-size: 15pt;
    }
}

.record1 {
    float: center;
    border: solid 4px #444444;
    width: 85%;
    padding: 1px 15px;
    margin-left: auto;
    margin-right: auto;
    box-shadow: 2px 1px 3px rgba(0, 0, 0, .2);
}

.record2{
    display: block;
    border : dashed 1px #444444;
    background :#F5F5F5;
    margin-left: auto;
    margin-right: auto;
    width: auto;
    padding: 10px;
}
[[/module]]
