:root {
    --themeColor: rgb(32, 18, 77)
    --accentColor: rgb(28, 69, 135)
}
body {
    background: background: ffffff;
    color: rgb(60, 120, 216);
}
a,a:visited {
    color: rgb(28, 69, 135);
}
h1,h2,h3,h4,h5,h6 {
    color: rgb(60, 120, 216);
}
blockquote {
    border: 5px inset rgb(61, 133, 198);
    background: #ffffff
}
div#header h1 a span {
    font-size: 0px;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#side-bar div.side-block {
    border: 1px solid rgb(28, 69, 135);
    box-shadow: 0 2px 6px rgba(28, 69, 135, 0.7);
    border-radius: 2px
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(28, 69, 135);
    border-color: rgb(28, 69, 135);
}
div.scpnet-interwiki-wrapper {
    filter: hue-rotate(350deg) saturate(0%);
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: rgb(208, 224, 227);
    color: rgb(109, 158, 235);
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: rgb(109, 158, 235);
    color: rgb(61, 133, 198);
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(32, 18, 77);
    color: rgb(61, 133, 198);
