[[module css]]
/* Aic div base styling */
.aic-art-folder-container {
  width: 100%;
  position: relative;
  scroll-margin-top: 30px;
}
.aic-art-folder-panel-overflow-container {
  width: 100%;
  border-radius: 30px;
  min-height: 500px;
  max-height: 850px;
  height: fit-content;
  display: inline-block;
  position: relative;
  overflow: hidden;
}
.aic-art-folder-panel-inner-container {
  min-height: 500px;
  max-height: 850px;
  height: fit-content;
  position: relative;
  display: grid;
  grid-template-rows: 100%;
  background-color: var(--aic-secondary-color);
}
.aic-art-folder-left {
  display: flex;
  flex-direction: column;
  vertical-align: text-top;
  height: 100%;
  max-height: 850px;
  grid-column: 1;
  grid-row: 1;
  background-color: var(--aic-primary-color);
  opacity: 0;
  transition: opacity ease-in-out 1s;
}
.aic-art-folder-right {
  display: flex;
  flex-direction: column;
  vertical-align: text-top;
  height: 100%;
  max-height: 850px;
  grid-column: 2;
  grid-row: 1;
  background-color: var(--aic-primary-color);
  opacity: 1;
  transition: opacity ease-in-out 1s;
}
.aic-art-desc-title {
  flex: initial;
  color: #fff;
  text-align: right;
  margin: 25px 10px 0 10px;
  font-size: 3em;
  text-shadow:
      -1px -1px 0 var(--aic-secondary-color),
      1px -1px 0 var(--aic-secondary-color),
      -1px 1px 0 var(--aic-secondary-color),
      1px 1px 0 var(--aic-secondary-color);
}
.aic-art-desc {
  flex: auto;
  border-radius: 0 0 20px 0;
  background-color: #fff;
  margin: 10px;
  padding: 10px 10px 10px 15%;
  overflow: auto;
}
.aic-art-avatar {
  position: absolute;
  width: 25%;
  left: 0;
  z-index: 1;
  transform: translateX(0) translateY(var(--avatar-translateY));
  bottom: 0;
}
.aic-art-avatar-1 {
  --avatar-translateY: -184%;
}
.aic-art-avatar-2 {
  --avatar-translateY: -92%;
}
.aic-art-avatar-3 {
  --avatar-translateY: 0;
}
.aic-art-full-body {
  position: relative;
  flex: auto;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 10px;
  border-radius: 20px 0 0 20px;
  overflow: hidden;
  background-color: #fff;
  font-size: 1.5em;
}
.aic-art-full-body img {
  max-height: 100%;
}
.aic-art-full-body-text {
  text-align: center;
}

/* Aic div panel toggle animations */
.aic-art-toggle .aic-art-avatar {
  transform: translateX(var(--avatar-translateX, 0)) translateY(var(--avatar-translateY));
  transition-property: left, transform;
  transition-duration: 1s;
  transition-timing-function: ease-in-out;
}
.aic-art-toggle .aic-art-folder-panel-overflow-container {
  border-radius: 0 30px 30px 30px;
  transition: border-radius ease-in-out 1s;
}
.aic-art-toggle .aic-art-folder-panel-inner-container {
  max-width: unset !important;
  width: 180%;
  left: -80%;
  grid-template-columns: 50% 50%;
  transition: left 1s ease-in-out;
}
.aic-art-no-toggle .aic-art-folder-panel-inner-container {
  grid-template-columns: 10% 90%;
}
.aic-art-toggle:target .aic-art-folder-panel-overflow-container {
  border-radius: 30px 0 30px 30px;
}
.aic-art-toggle:target .aic-art-folder-panel-inner-container {
  left: 0;
}
.aic-art-toggle:target .aic-art-folder-left {
  opacity: 1;
}
.aic-art-toggle:target .aic-art-folder-right {
  opacity: 0;
}
.aic-art-toggle:target .aic-art-avatar {
  --avatar-translateX: -100%;
  left: 100%;
}

/* Aic div navigation and animations */
.aic-art-nav {
  border-radius: 30px 30px 0 0;
  position: relative;
  height: 40px;
  width: 100%;
  overflow: hidden;
  z-index: 1;
}
.aic-art-nav a,
.aic-art-nav a:hover,
.aic-art-nav a:visited {
  display: inline-block;
  position: absolute;
  top: 0;
  width: 20%;
  height: 40px;
  color: #fff;
  background-color: var(--aic-secondary-color);
  transition-property: left, right, transform, opacity;
  transition-timing-function: ease-in-out;
  transition-duration: 1s;
  z-index: -1;
}
.aic-art-nav a::before {
  position: absolute;
  top: 50%;
  text-shadow:
      -1px 0 0 var(--aic-secondary-color),
      1px 0 0 var(--aic-secondary-color),
      0 -1px 0 var(--aic-secondary-color),
      0 1px 0 var(--aic-secondary-color);
}
.aic-art-nav a::after {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  z-index: -2;
  width: 100%;
  height: 100%;
  background-color: var(--aic-primary-color);
}
.aic-art-nav-right,
.aic-art-nav-right:hover,
.aic-art-nav-right:visited {
  border-radius: 0 30px 0 0;
  right: 80%;
  transform: translateY(100%);
  opacity: 0;
}
.aic-art-nav-left,
.aic-art-nav-left:hover,
.aic-art-nav-left:visited {
  border-radius: 30px 0 0 0;
  left: 0;
  transform: translateY(0);
  opacity: 1;
}
.aic-art-toggle:target .aic-art-nav-right,
.aic-art-toggle:target .aic-art-nav-right:hover,
.aic-art-toggle:target .aic-art-nav-right:visited {
  right: 0;
  transform: translateY(0);
  opacity: 1;
}
.aic-art-toggle:target .aic-art-nav-left,
.aic-art-toggle:target .aic-art-nav-left:hover,
.aic-art-toggle:target .aic-art-nav-left:visited {
  left: 80%;
  transform: translateY(100%);
  opacity: 0;
}
.aic-art-nav-right::after {
  border-radius: 0 30px 0 0;
  right: 50%;
}
.aic-art-nav-left::after {
  border-radius: 30px 0 0 0;
  left: 50%;
}
.aic-art-nav-right::before {
  left: 0;
  transform: translateX(-50%) translateY(-50%);
}
.aic-art-nav-left::before {
  right: 0;
  transform: translateX(50%) translateY(-50%);
}

/* Aic colors */
:root {
  /* fallback colors */
  --aic-primary-color: #ccc;
  --aic-secondary-color: #888;
}

#u-aic-almond {
  --aic-primary-color: #e0f1bf;
  --aic-secondary-color: #6a7460;
}

[[/module]]

