    background: -webkit-linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background-size: 200% 200%;
    animation: Gradient 3.5s ease-in-out infinite;
    -webkit-background-clip: text !important;
    -webkit-text-fill-color: transparent;
}
#page-title {
    display: none;
}
#breadcrumbs {
    margin: 0;
}

/* Character Introduction */

.CIbox-wrapper {
    box-shadow: 0px 0px 4px #292929;
    position: relative;
    background: #fff;
    padding: 10px 20px;
    margin: 20px;
}
.CIbox-top {
    position: relative;
    color: #203a43;
    display: flex;
    justify-content: space-between;
    min-height: 60px;
    font-family: "noto serif jp";
    font-weight: 600;
}
.CIbox-top-name {
    position: relative;
    color: #203a43;
    font-size: 25px;
    width: 70%;
}
.CIbox-top-name .rt{
    font-size:12px;
}
.CIbox-top-name p {
    position: relative;
    bottom: 25px;
}
.CIbox-top-name a {
    color:#0f7299;
}
.CIbox-top-affiliation {
    position: relative;
    color: #203a43;
    width: 20%;
    font-size:15px;
}
.CIbox-top-affiliation p {
    position: absolute;
    bottom: 7px;
    right: 0;
}
.CIbox-main {
    position: relative;
    color: #203a43;
    margin-top: -10px;
}
.CIbox-main-quote{
    font-family: "noto serif jp";
    font-style: italic;
}

@media (max-width: 479px) {
    .CIbox-wrapper {
        margin: 20px 0;
    }
    .CIbox-top {
        justify-content: space-around;
        flex-direction: column;
        align-items: flex-end;
    }
    .CIbox-top-name {
        width: 100%;
        font-size: 20px;
    }

    .CIbox-top-name p, .CIbox-top-affiliation p {
        margin-block-start: 0;
        margin-block-end: 0;
        position: unset;
    }
}

[[/module]]

