[[module CSS]]
/*
    [no title]
    Created at 2020
    Created by dog_punch
    CC BY-SA 3.0
*/
a:active {
    background: none;
}
hr {
    height: 0.5px;
}
.tab-container-wrap {
    display: flex;
    overflow-X: hidden;
    width: 100%;
}

.tab-container {
    flex: 1 1 100%;
    display: flex;
    flex-wrap: nowrap;
    transition: 1.5s cubic-bezier(0.35, -0.18, 0.65, 1.21);
}

#u-tab-1:target~.tab-container-wrap .tab-container {
    transform: translateX(-100%);
}

#u-tab-2:target~.tab-container-wrap .tab-container {
    transform: translateX(-200%);
}

#u-tab-3:target~.tab-container-wrap .tab-container {
    transform: translateX(-300%);
}

#u-tab-4:target~.tab-container-wrap .tab-container {
    transform: translateX(-400%);
}

.tab-content {
    width: 100%;
    flex: 1 0 100%;
    box-sizing: border-box;
    padding: 0 3px;
    overflow: hidden;
}

div[id^="u-tab"] {
    position: fixed;
    pointer-events: none;
    color: transparent;
    top: 0%;
}

.tab-buttons {
    display: flex;
    justify-content: center;
}

.tab-buttons a {
    color: #2c5364 !Important;
    border-bottom: 1px solid #2c5364;
    display: inline-block;
    height: 30px;
    font-size: 13px;
    line-height: 25px;
    text-align: center;
    margin: 0 8px;
    padding: 0 12px;
    text-decoration: none;
    transition: .3s;
    font-family: 'Noto Serif JP', monospace;
}

.tab-buttons a:first-of-type {
    margin-left: 0;
}

.tab-buttons a:last-of-type {
    margin-right: 0;
}

.tab-buttons a:hover {
    opacity: 0.7;
}
@keyframes one {
    0% {opacity: 0;transform: translateY(30px);}
    100%{opacity: 1;transform: translateY(0);}
}

#page-title {
    text-align: center;
    font-size: 0px;
    padding: 0 0 3px;
    margin: 0 0 10px;
}
@keyframes Gradient {
0% {
    background-position: 0% 50%
}
50% {
    background-position: 100% 50%
}
100% {
    background-position: 0% 50%
}
}
#page-title:before {
    content: '대화부 허브';
    font-family: 'Noto Serif JP', 'UnBatangBold';
    font-size: 35px;
    background: #2c3e50;
    background: -webkit-linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background-size: 200% 200%;
    animation: Gradient 3.5s ease-in-out infinite;
    -webkit-background-clip: text !important;
    -webkit-text-fill-color: transparent;
}

#main-content {
    padding: 0 1em 1em 1em;
}
.body {
    text-align: center;
    font-family: 'noto serif jp';
    position: relative;
}
.fade {
    animation: fadeout 5.5s ease-in-out 12s forwards !important;
    overflow: hidden;
    z-index:0;
}
@keyframes fadeout {
99% {
    opacity:0;
    pointer-events: auto;
}
100% {
    opacity:0;
    pointer-events: none;
}
}
.d-title {
    animation-name: fade;
    animation-duration: 4s;
    animation-iteration-count: 1;
    animation-fill-mode: both;
    animation-delay: 14s;
    width: 105%;
    position: absolute;
      left:50%;
      top: 50%;
      transform:translate(-50%,-50%);
    z-index:0;
}
@keyframes fade {
0% {
    opacity: 0;
}
100% {
    opacity: 1;
}
}
@media (max-width:600px) {
.media-none {
    display: none;
}
}
.list-body {
    text-align:center;
}
.list-title a{
    color: #203A43;
    background: -webkit-linear-gradient(to right, #c31432, #240b36);
    background: linear-gradient(to right, #c31432, #240b36);
    font-family: 'noto serif jp';
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-size: 120%;
  word-wrap:break-all;


}
div.list-body hr {
border: 0;
    height: 0.8px;
    background-image: -webkit-linear-gradient(left, #dddddd00, rgb(100 100 100 / 0.8), #dddddd00);
    background-image: -moz-linear-gradient(left, #dddddd00, rgb(100 100 100 / 0.8), #dddddd00);
    background-image: -ms-linear-gradient(left, #dddddd00, rgb(100 100 100 / 0.8), #dddddd00);
    background-image: -o-linear-gradient(left, #dddddd00, rgb(100 100 100 / 0.8), #dddddd00);
    background-color: transparent;
}
div.list-body em{
font-family: 'noto serif jp';
    -webkit-mask: linear-gradient(to left,transparent,#333,#fff 98%);
    font-style: normal;
}
.d-fake-title {
    text-align: center;
    font-size: 0px;
    padding: 0 0 3px;
    margin: 0 0 10px;
    border-bottom: 1px solid #bbb;
    color: #203A43;
    font-family: 'noto serif jp';
    font-weight: 600;
}
.d-fake-title:before {
    content: '대화부 허브';
    font-family: 'Noto Serif JP', 'UnBatangBold';
    font-size: 35px;
    background: #2c3e50;
    background: -webkit-linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background-size: 200% 200%;
    animation: Gradient 3.5s ease-in-out infinite;
    -webkit-background-clip: text !important;
    -webkit-text-fill-color: transparent;
}
#page-title {
    display: none;
}
#breadcrumbs {
    margin: 0;
}

/* Character Introduction */

.CIbox-wrapper {
    box-shadow: 0px 0px 4px #292929;
    position: relative;
    background: #fff;
    padding: 10px 20px;
    margin: 20px;
}
.CIbox-top {
    position: relative;
    color: #203a43;
    display: flex;
    justify-content: space-between;
    min-height: 60px;
    font-family: "noto serif jp";
    font-weight: 600;
}
.CIbox-top-name {
    position: relative;
    color: #203a43;
    font-size: 25px;
    width: 70%;
}
.CIbox-top-name .rt{
    font-size:12px;
}
.CIbox-top-name p {
    position: relative;
    bottom: 25px;
}
.CIbox-top-name a {
    color:#0f7299;
}
.CIbox-top-affiliation {
    position: relative;
    color: #203a43;
    width: 20%;
    font-size:15px;
}
.CIbox-top-affiliation p {
    position: absolute;
    bottom: 7px;
    right: 0;
}
.CIbox-main {
    position: relative;
    color: #203a43;
    margin-top: -10px;
}
.CIbox-main-quote{
    font-family: "noto serif jp";
    font-style: italic;
}

@media (max-width: 479px) {
    .CIbox-wrapper {
        margin: 20px 0;
    }
    .CIbox-top {
        justify-content: space-around;
        flex-direction: column;
        align-items: flex-end;
    }
    .CIbox-top-name {
        width: 100%;
        font-size: 20px;
    }

    .CIbox-top-name p, .CIbox-top-affiliation p {
        margin-block-start: 0;
        margin-block-end: 0;
        position: unset;
    }
}

[[/module]]

