.tab-content {
    width: 100%;
    flex: 1 0 100%;
    box-sizing: border-box;
    padding: 0 3px;
    overflow: hidden;
}

div[id^="u-tab"] {
    position: fixed;
    pointer-events: none;
    color: transparent;
    top: 0%;
}

.tab-buttons {
    display: flex;
    justify-content: center;
}

.tab-buttons a {
    color: #2c5364 !Important;
    border-bottom: 1px solid #2c5364;
    display: inline-block;
    height: 30px;
    font-size: 13px;
    line-height: 25px;
    text-align: center;
    margin: 0 8px;
    padding: 0 12px;
    text-decoration: none;
    transition: .3s;
    font-family: 'Noto Serif JP', monospace;
}

.tab-buttons a:first-of-type {
    margin-left: 0;
}

.tab-buttons a:last-of-type {
    margin-right: 0;
}

.tab-buttons a:hover {
    opacity: 0.7;
}
@keyframes one {
    0% {opacity: 0;transform: translateY(30px);}
    100%{opacity: 1;transform: translateY(0);}
}

#page-title {
    text-align: center;
    font-size: 0px;
    padding: 0 0 3px;
    margin: 0 0 10px;
}
@keyframes Gradient {
0% {
    background-position: 0% 50%
}
50% {
    background-position: 100% 50%
}
100% {
    background-position: 0% 50%
}
}
#page-title:before {
    content: '대화부 허브';
    font-family: 'Noto Serif JP', 'UnBatangBold';
    font-size: 35px;
    background: #2c3e50;
    background: -webkit-linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background: linear-gradient(to right, #0f2027, #203a43, #2c5364, #0f2027, #203a43, #2c5364);
    background-size: 200% 200%;
    animation: Gradient 3.5s ease-in-out infinite;
    -webkit-background-clip: text !important;
    -webkit-text-fill-color: transparent;
}

