    padding: 0 1em 1em 1em;
}
.body {
    text-align: center;
    font-family: 'noto serif jp';
    position: relative;
}
.fade {
    animation: fadeout 5.5s ease-in-out 12s forwards !important;
    overflow: hidden;
    z-index:0;
}
@keyframes fadeout {
99% {
    opacity:0;
    pointer-events: auto;
}
100% {
    opacity:0;
    pointer-events: none;
}
}
.d-title {
    animation-name: fade;
    animation-duration: 4s;
    animation-iteration-count: 1;
    animation-fill-mode: both;
    animation-delay: 14s;
    width: 105%;
    position: absolute;
      left:50%;
      top: 50%;
      transform:translate(-50%,-50%);
    z-index:0;
}
@keyframes fade {
0% {
    opacity: 0;
}
100% {
    opacity: 1;
}
}
